import React, { useState } from "react";
import TopNav from "./components/TopNav";
import Tagging from "./components/Tagging";
import Validation from "./components/Validation";
import Summary from "./components/Summary";
import { Box, ThemeProvider, createTheme } from "@mui/material";
const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    background: {
      default: '#0a1929',
      paper: '#111827'
    },
    text: {
      primary: '#fff',
      secondary: '#90caf9'
    }
  }
});

function App() {
  const [selected, setSelected] = useState("summary");
  const [workflowRun, setWorkflowRun] = useState(false);

  const handleSidebarSelect = (option) => {
    setSelected(option);
  };

  const handleWorkflowRun = () => {
    setWorkflowRun(true);
    setTimeout(() => setWorkflowRun(false), 2000); // Simulate workflow
  };

  // Example usage of BACKEND_URL (remove this in production)
  // console.log("Backend URL:", BACKEND_URL);
  return (
    <ThemeProvider theme={darkTheme}>
      <Box sx={{ height: "100vh", background: darkTheme.palette.background.default }}>
        <TopNav selected={selected} onSelect={handleSidebarSelect} />
        <Box sx={{ p: 3, background: darkTheme.palette.background.default, minHeight: 'calc(100vh - 64px)', overflowY: 'auto' }}>
          {selected === "summary" && <Summary />}
          {selected === "tagging" && <Tagging />}
          {selected === "validation" && <Validation />}
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default App;
