import React from "react";
import { AppBar, Toolbar, Tabs, Tab, Box } from "@mui/material";
import LabelIcon from '@mui/icons-material/Label';
import VerifiedIcon from '@mui/icons-material/Verified';
import SummarizeIcon from '@mui/icons-material/Summarize';

const options = [
  { key: "summary", label: "User Guide", icon: <SummarizeIcon /> },
  { key: "tagging", label: "Potential Risk Flagger", icon: <LabelIcon /> },
  { key: "validation", label: "Discrepancy Validator", icon: <VerifiedIcon /> },
];

function TopNav({ selected, onSelect }) {
  const value = options.findIndex(opt => opt.key === selected);
  return (
    <AppBar position="static" sx={{ background: '#111827', boxShadow: 0 }}>
      <Toolbar>
        <Tabs
          value={value}
          onChange={(e, idx) => onSelect(options[idx].key)}
          textColor="inherit"
          indicatorColor="primary"
          sx={{ minHeight: 64 }}
        >
          {options.map(opt => (
            <Tab
              key={opt.key}
              icon={opt.icon}
              iconPosition="start"
              label={opt.label}
              sx={{
                color: value === options.findIndex(o => o.key === opt.key) ? '#90caf9' : '#fff',
                fontWeight: value === options.findIndex(o => o.key === opt.key) ? 700 : 400,
                minHeight: 64,
                fontSize: '1rem',
                px: 3,
                textTransform: 'none',
              }}
            />
          ))}
        </Tabs>
      </Toolbar>
    </AppBar>
  );
}

export default TopNav;
