import React, { useEffect, useState, useMemo } from "react";
import { 
  Box, 
  Grid, 
  Typography, 
  CircularProgress, 
  FormControl, 
  InputLabel, 
  Select, 
  MenuItem, 
  Paper, 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  Card, 
  CardContent, 
  Tabs, 
  Tab, 
  Chip,
  IconButton,
  Tooltip,
  Skeleton,
  Fade,
  Slide,
  Zoom,
  Grow,
  useTheme,
  alpha,
  LinearProgress,
  Container,
  Stack,
  Avatar,
  Badge,
  Divider
} from "@mui/material";
import { 
  PieChart, 
  Pie, 
  Cell, 
  Tooltip as RechartsTooltip, 
  Legend, 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  ResponsiveContainer, 
  LineChart, 
  Line, 
  CartesianGrid, 
  ComposedChart,
  Area,
  AreaChart,
  RadialBarChart,
  RadialBar,
  Treemap
} from "recharts";
import { 
  AttachMoney as AttachMoneyIcon, 
  ShowChart as ShowChartIcon, 
  MonetizationOn as MonetizationOnIcon, 
  Savings as SavingsIcon,
  TrendingUp as TrendingUpIcon,
  TrendingDown as TrendingDownIcon,
  Assessment as AssessmentIcon,
  ErrorOutline as ErrorIcon,
  CheckCircleOutline as CheckIcon,
  FilterList as FilterIcon,
  Refresh as RefreshIcon,
  Speed as SpeedIcon,
  Timeline as TimelineIcon,
  Analytics as AnalyticsIcon,
  Dashboard as DashboardIcon,
  Insights as InsightsIcon,
  AutoGraph as AutoGraphIcon,
  DonutLarge as DonutLargeIcon,
  DataUsage as DataUsageIcon
} from '@mui/icons-material';
import { keyframes, styled } from '@mui/material/styles';
import { BACKEND_URL } from "../utils/backend";
import PenaltyTable from "./PenaltyTable";



// Premium color palette with neon accents
const NEON_COLORS = [
  "#00ff88", "#ff0080", "#0088ff", "#ff8800", "#8800ff", "#00ffff", "#ff4444", "#44ff44"
];

const GRADIENT_COLORS = [
  "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
  "linear-gradient(135deg, #f093fb 0%, #f5576c 100%)",
  "linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)",
  "linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)",
  "linear-gradient(135deg, #fa709a 0%, #fee140 100%)",
  "linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)",
  "linear-gradient(135deg, #ff9a9e 0%, #fecfef 100%)",
  "linear-gradient(135deg, #a18cd1 0%, #fbc2eb 100%)"
];

// Keyframe animations
const glow = keyframes`
  0% { box-shadow: 0 0 20px rgba(0, 255, 136, 0.3); }
  50% { box-shadow: 0 0 40px rgba(0, 255, 136, 0.6), 0 0 60px rgba(0, 255, 136, 0.4); }
  100% { box-shadow: 0 0 20px rgba(0, 255, 136, 0.3); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const slideUp = keyframes`
  from { opacity: 0; transform: translateY(30px); }
  to { opacity: 1; transform: translateY(0); }
`;

const float = keyframes`
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
`;

// Styled components for premium UI
const GlassmorphicCard = styled(Card)(({ theme, variant = 'primary' }) => ({
  background: variant === 'primary' 
    ? 'linear-gradient(145deg, rgba(15, 23, 42, 0.8) 0%, rgba(30, 41, 59, 0.6) 100%)'
    : 'linear-gradient(145deg, rgba(17, 24, 39, 0.9) 0%, rgba(31, 41, 55, 0.7) 100%)',
  backdropFilter: 'blur(20px)',
  border: '1px solid rgba(255, 255, 255, 0.1)',
  borderRadius: '24px',
  boxShadow: variant === 'primary'
    ? '0 20px 60px rgba(0, 0, 0, 0.5), inset 0 1px 0 rgba(255, 255, 255, 0.1)'
    : '0 25px 80px rgba(0, 0, 0, 0.6), inset 0 1px 0 rgba(255, 255, 255, 0.05)',
  transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
  position: 'relative',
  overflow: 'hidden',
  '&:before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    height: '2px',
    background: 'linear-gradient(90deg, transparent, rgba(0, 255, 136, 0.8), transparent)',
    opacity: 0,
    transition: 'opacity 0.3s ease',
  },
  '&:hover': {
    transform: 'translateY(-8px) scale(1.02)',
    boxShadow: '0 30px 100px rgba(0, 0, 0, 0.7), 0 0 60px rgba(0, 255, 136, 0.1), inset 0 1px 0 rgba(255, 255, 255, 0.2)',
    border: '1px solid rgba(0, 255, 136, 0.3)',
    '&:before': {
      opacity: 1,
    }
  },
  animation: `${slideUp} 0.6s ease-out`,
}));

const NeonIconBox = styled(Box)(({ theme, color = '#00ff88' }) => ({
  width: 56,
  height: 56,
  borderRadius: '16px',
  background: `linear-gradient(135deg, ${alpha(color, 0.2)} 0%, ${alpha(color, 0.05)} 100%)`,
  border: `1px solid ${alpha(color, 0.3)}`,
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  position: 'relative',
  transition: 'all 0.3s ease',
  '&:before': {
    content: '""',
    position: 'absolute',
    inset: -2,
    borderRadius: '18px',
    background: `linear-gradient(45deg, ${color}, transparent, ${color})`,
    opacity: 0,
    transition: 'opacity 0.3s ease',
    zIndex: -1,
  },
  '&:hover': {
    transform: 'scale(1.1)',
    boxShadow: `0 0 30px ${alpha(color, 0.6)}`,
    '&:before': {
      opacity: 1,
    }
  }
}));

const AnimatedNumber = styled(Typography)(({ theme }) => ({
  background: 'linear-gradient(135deg, #00ff88 0%, #00ccff 100%)',
  backgroundClip: 'text',
  WebkitBackgroundClip: 'text',
  WebkitTextFillColor: 'transparent',
  fontWeight: 800,
  fontSize: '2.5rem',
  lineHeight: 1,
  animation: `${pulse} 2s ease-in-out infinite`,
}));

const GradientText = styled(Typography)(({ gradient = 'linear-gradient(135deg, #00ff88 0%, #00ccff 100%)' }) => ({
  background: gradient,
  backgroundClip: 'text',
  WebkitBackgroundClip: 'text',
  WebkitTextFillColor: 'transparent',
  fontWeight: 700,
}));

const FloatingCard = styled(GlassmorphicCard)(({ theme, delay = 0 }) => ({
  animation: `${float} 3s ease-in-out infinite ${delay}s, ${slideUp} 0.8s ease-out`,
}));

// Premium animated counter with smooth transitions
const PremiumCounter = ({ value, duration = 2000, prefix = '', suffix = '', gradient = 'linear-gradient(135deg, #00ff88 0%, #00ccff 100%)' }) => {
  const [count, setCount] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  
  useEffect(() => {
    setIsVisible(true);
    let start = 0;
    const end = parseFloat(value) || 0;
    if (start === end) {
      setCount(end);
      return;
    }
    
    const startTime = Date.now();
    const timer = setInterval(() => {
      const elapsed = Date.now() - startTime;
      const progress = Math.min(elapsed / duration, 1);
      
      // Easing function for smooth animation
      const easeOutCubic = 1 - Math.pow(1 - progress, 3);
      const current = start + (end - start) * easeOutCubic;
      
      setCount(current);
      
      if (progress >= 1) {
        setCount(end);
        clearInterval(timer);
      }
    }, 16);
    
    return () => clearInterval(timer);
  }, [value, duration]);
  
  return (
    <Zoom in={isVisible} timeout={800}>
      <GradientText 
        variant="h3" 
        gradient={gradient}
        sx={{ 
          fontFamily: '"Inter", "Roboto", sans-serif',
          fontWeight: 900,
          fontSize: { xs: '1.8rem', sm: '2.2rem', md: '2.8rem' },
          textShadow: '0 0 20px rgba(0, 255, 136, 0.3)',
          animation: `${glow} 3s ease-in-out infinite`,
        }}
      >
        {prefix}{Math.round(count).toLocaleString()}{suffix}
      </GradientText>
    </Zoom>
  );
};

// Premium KPI Card with advanced styling
const PremiumKPICard = ({ 
  title, 
  value, 
  subtitle, 
  icon: Icon, 
  color = '#00ff88', 
  trend, 
  trendDirection = 'up',
  isLoading,
  delay = 0 
}) => {
  const [isHovered, setIsHovered] = useState(false);
  
  if (isLoading) {
    return (
      <FloatingCard variant="secondary" delay={delay} sx={{ height: 180 }}>
        <CardContent sx={{ p: 4, height: '100%', display: 'flex', flexDirection: 'column' }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 3 }}>
            <Skeleton variant="text" width="60%" height={24} sx={{ borderRadius: 2 }} />
            <Skeleton variant="circular" width={48} height={48} />
          </Box>
          <Skeleton variant="text" width="40%" height={48} sx={{ mb: 2, borderRadius: 2 }} />
          <Box sx={{ mt: 'auto', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Skeleton variant="text" width="70%" height={20} sx={{ borderRadius: 2 }} />
            <Skeleton variant="rectangular" width={60} height={24} sx={{ borderRadius: 3 }} />
          </Box>
        </CardContent>
      </FloatingCard>
    );
  }
  
  return (
    <Slide direction="up" in timeout={600 + delay * 200}>
      <FloatingCard 
        delay={delay}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        sx={{ 
          height: 180,
          cursor: 'pointer',
          '&:hover': {
            '& .icon-glow': {
              boxShadow: `0 0 40px ${alpha(color, 0.8)}`,
              '&:before': {
                opacity: 1,
              }
            },
            '& .trend-chip': {
              transform: 'scale(1.1)',
            }
          }
        }}
      >
        <CardContent sx={{ p: 4, height: '100%', display: 'flex', flexDirection: 'column', position: 'relative' }}>
          {/* Floating background orb */}
          <Box
            sx={{
              position: 'absolute',
              top: -20,
              right: -20,
              width: 100,
              height: 100,
              borderRadius: '50%',
              background: `radial-gradient(circle, ${alpha(color, 0.1)} 0%, transparent 70%)`,
              transition: 'all 0.6s ease',
              transform: isHovered ? 'scale(1.5)' : 'scale(1)',
            }}
          />
          
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 3, zIndex: 1 }}>
            <Box>
              <GradientText 
                variant="subtitle1" 
                gradient={`linear-gradient(135deg, ${color} 0%, ${alpha(color, 0.7)} 100%)`}
                sx={{ 
                  fontWeight: 700,
                  fontSize: '0.9rem',
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  mb: 0.5
                }}
              >
                {title}
              </GradientText>
              <Typography 
                variant="body2" 
                sx={{ 
                  color: alpha('#ffffff', 0.6),
                  fontSize: '0.75rem',
                  fontWeight: 500
                }}
              >
                Real-time Analytics
              </Typography>
            </Box>
            
            <NeonIconBox 
              color={color} 
              className="icon-glow"
              sx={{
                animation: `${float} 4s ease-in-out infinite ${delay * 0.5}s`,
              }}
            >
              <Icon sx={{ color, fontSize: 24, filter: `drop-shadow(0 0 10px ${alpha(color, 0.7)})` }} />
            </NeonIconBox>
          </Box>
          
          <Box sx={{ mb: 'auto', zIndex: 1 }}>
            <PremiumCounter 
              value={typeof value === 'string' ? value.replace(/[^0-9.]/g, '') : value}
              suffix={typeof value === 'string' && value.includes('%') ? '%' : ''}
              gradient={`linear-gradient(135deg, ${color} 0%, ${alpha(color, 0.8)} 50%, #ffffff 100%)`}
              duration={1500 + delay * 200}
            />
          </Box>
          
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mt: 2, zIndex: 1 }}>
            <Typography 
              variant="body2" 
              sx={{ 
                color: alpha('#ffffff', 0.7),
                fontSize: '0.8rem',
                fontWeight: 500,
                maxWidth: '60%'
              }}
            >
              {subtitle}
            </Typography>
            
            {trend && (
              <Chip 
                className="trend-chip"
                size="small"
                icon={trendDirection === 'up' ? 
                  <TrendingUpIcon sx={{ fontSize: 16 }} /> : 
                  <TrendingDownIcon sx={{ fontSize: 16 }} />
                }
                label={trend}
                sx={{
                  background: trendDirection === 'up' ? 
                    'linear-gradient(135deg, #00ff88 0%, #00cc66 100%)' :
                    'linear-gradient(135deg, #ff4444 0%, #cc3333 100%)',
                  color: '#000',
                  fontWeight: 700,
                  fontSize: '0.75rem',
                  height: 28,
                  transition: 'all 0.3s ease',
                  border: `1px solid ${alpha(trendDirection === 'up' ? '#00ff88' : '#ff4444', 0.3)}`,
                  '& .MuiChip-icon': { 
                    color: '#000', 
                    fontSize: 16,
                    filter: 'drop-shadow(0 0 5px rgba(0,0,0,0.3))'
                  }
                }}
              />
            )}
          </Box>
        </CardContent>
      </FloatingCard>
    </Slide>
  );
};

// Premium Chart Card with advanced styling
const PremiumChartCard = ({ 
  title, 
  subtitle, 
  children, 
  isLoading, 
  actions, 
  height = 450,
  delay = 0,
  icon: Icon
}) => {
  const [isHovered, setIsHovered] = useState(false);
  
  if (isLoading) {
    return (
      <FloatingCard 
        variant="secondary" 
        delay={delay} 
        sx={{ height }}
      >
        <CardContent sx={{ p: 4, height: '100%', display: 'flex', flexDirection: 'column' }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 3 }}>
            <Box>
              <Skeleton variant="text" width="60%" height={32} sx={{ borderRadius: 2, mb: 1 }} />
              <Skeleton variant="text" width="40%" height={20} sx={{ borderRadius: 2 }} />
            </Box>
            <Skeleton variant="circular" width={40} height={40} />
          </Box>
          <Skeleton 
            variant="rectangular" 
            width="100%" 
            sx={{ 
              flexGrow: 1, 
              borderRadius: 3,
              background: 'linear-gradient(90deg, rgba(255,255,255,0.1) 25%, rgba(255,255,255,0.2) 50%, rgba(255,255,255,0.1) 75%)',
              backgroundSize: '200% 100%',
              animation: 'shimmer 2s infinite',
              '@keyframes shimmer': {
                '0%': { backgroundPosition: '-200% 0' },
                '100%': { backgroundPosition: '200% 0' }
              }
            }} 
          />
        </CardContent>
      </FloatingCard>
    );
  }
  
  return (
    <Grow in timeout={800 + delay * 300}>
      <FloatingCard 
        delay={delay}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        sx={{ 
          height,
          cursor: 'pointer',
          '&:hover': {
            '& .chart-header': {
              transform: 'translateY(-2px)',
            },
            '& .chart-actions': {
              opacity: 1,
              transform: 'translateX(0)',
            }
          }
        }}
      >
        <CardContent sx={{ p: 4, height: '100%', display: 'flex', flexDirection: 'column', position: 'relative' }}>
          {/* Animated background pattern */}
          <Box
            sx={{
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: `radial-gradient(circle at 80% 20%, ${alpha('#00ff88', 0.03)} 0%, transparent 50%),
                          radial-gradient(circle at 20% 80%, ${alpha('#0088ff', 0.03)} 0%, transparent 50%)`,
              transition: 'all 0.6s ease',
              transform: isHovered ? 'scale(1.1)' : 'scale(1)',
              zIndex: 0,
            }}
          />
          
          <Box 
            className="chart-header"
            sx={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'flex-start', 
              mb: 4,
              transition: 'all 0.3s ease',
              zIndex: 1
            }}
          >
            <Box>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 1 }}>
                {Icon && (
                  <NeonIconBox color="#00ff88" sx={{ width: 32, height: 32 }}>
                    <Icon sx={{ fontSize: 18 }} />
                  </NeonIconBox>
                )}
                <GradientText 
                  variant="h5" 
                  gradient="linear-gradient(135deg, #00ff88 0%, #00ccff 50%, #0088ff 100%)"
                  sx={{ 
                    fontWeight: 800,
                    fontSize: { xs: '1.1rem', sm: '1.3rem', md: '1.5rem' },
                    textShadow: '0 0 20px rgba(0, 255, 136, 0.3)',
                  }}
                >
                  {title}
                </GradientText>
              </Box>
              {subtitle && (
                <Typography 
                  variant="body2" 
                  sx={{ 
                    color: alpha('#ffffff', 0.6),
                    fontSize: '0.85rem',
                    fontWeight: 500,
                    ml: Icon ? 5 : 0
                  }}
                >
                  {subtitle}
                </Typography>
              )}
            </Box>
            
            {actions && (
              <Box 
                className="chart-actions"
                sx={{ 
                  display: 'flex', 
                  gap: 1,
                  opacity: 0.7,
                  transform: 'translateX(10px)',
                  transition: 'all 0.3s ease'
                }}
              >
                {actions.map((action, index) => (
                  <IconButton
                    key={index}
                    size="small"
                    sx={{
                      background: 'linear-gradient(135deg, rgba(0, 255, 136, 0.1) 0%, rgba(0, 136, 255, 0.1) 100%)',
                      border: '1px solid rgba(0, 255, 136, 0.2)',
                      color: '#00ff88',
                      '&:hover': {
                        background: 'linear-gradient(135deg, rgba(0, 255, 136, 0.2) 0%, rgba(0, 136, 255, 0.2) 100%)',
                        boxShadow: '0 0 20px rgba(0, 255, 136, 0.4)',
                        transform: 'scale(1.1)',
                      }
                    }}
                  >
                    {action}
                  </IconButton>
                ))}
              </Box>
            )}
          </Box>
          
          <Box sx={{ flexGrow: 1, position: 'relative', zIndex: 1 }}>
            {children}
          </Box>
        </CardContent>
      </FloatingCard>
    </Grow>
  );
};

// Premium Table Components
const PremiumTableContainer = styled(Box)(({ theme }) => ({
  background: 'linear-gradient(135deg, rgba(30, 34, 46, 0.95) 0%, rgba(20, 24, 36, 0.98) 100%)',
  backdropFilter: 'blur(30px)',
  border: '1px solid rgba(0, 255, 136, 0.1)',
  borderRadius: '20px',
  overflow: 'hidden',
  boxShadow: `
    0 20px 40px rgba(0, 0, 0, 0.4),
    inset 0 1px 0 rgba(255, 255, 255, 0.1),
    0 0 30px rgba(0, 255, 136, 0.05)
  `,
  position: 'relative',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    height: '2px',
    background: 'linear-gradient(90deg, #00ff88, #00ccff, #0088ff, #ff0080)',
    backgroundSize: '300% 100%',
    animation: 'rainbow-border 3s ease-in-out infinite',
  },
  '@keyframes rainbow-border': {
    '0%, 100%': { backgroundPosition: '0% 50%' },
    '50%': { backgroundPosition: '100% 50%' },
  }
}));

const PremiumTableCell = styled(TableCell)(({ theme, isHeader }) => ({
  backgroundColor: 'transparent',
  color: isHeader ? '#00ff88' : '#ffffff',
  fontWeight: isHeader ? 700 : 500,
  borderBottom: isHeader 
    ? '2px solid rgba(0, 255, 136, 0.3)' 
    : '1px solid rgba(255, 255, 255, 0.05)',
  padding: '18px 24px',
  fontSize: isHeader ? '0.95rem' : '0.875rem',
  fontFamily: 'Inter, system-ui, sans-serif',
  letterSpacing: isHeader ? '0.5px' : '0.25px',
  textTransform: isHeader ? 'uppercase' : 'none',
  position: 'relative',
  ...(isHeader && {
    textShadow: '0 0 10px rgba(0, 255, 136, 0.3)',
    '&::after': {
      content: '""',
      position: 'absolute',
      bottom: '-2px',
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '2px',
      background: 'linear-gradient(90deg, transparent, #00ff88, transparent)',
      animation: 'glow-line 2s ease-in-out infinite alternate',
    }
  }),
  '@keyframes glow-line': {
    '0%': { opacity: 0.3, width: '30%' },
    '100%': { opacity: 0.8, width: '80%' },
  }
}));

const PremiumTableRow = styled(TableRow)(({ theme, isClickable }) => ({
  background: 'linear-gradient(90deg, rgba(30, 34, 46, 0.3) 0%, rgba(25, 29, 41, 0.5) 100%)',
  position: 'relative',
  cursor: isClickable ? 'pointer' : 'default',
  '&::before': {
    content: '""',
    position: 'absolute',
    left: 0,
    top: 0,
    bottom: 0,
    width: '3px',
    background: 'linear-gradient(180deg, #00ff88, #0088ff)',
    opacity: 0,
    transition: 'opacity 0.3s ease',
  },
  '&:nth-of-type(even)': {
    background: 'linear-gradient(90deg, rgba(25, 29, 41, 0.3) 0%, rgba(35, 39, 51, 0.4) 100%)',
  },
  '&:hover': {
    background: 'linear-gradient(90deg, rgba(0, 255, 136, 0.08) 0%, rgba(0, 136, 255, 0.05) 100%)',
    transform: 'translateX(2px)',
    '&::before': {
      opacity: 1,
    },
    '& .action-button': {
      opacity: 1,
      transform: 'translateX(0)',
    },
    '& .row-glow': {
      opacity: 1,
    }
  },
  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
  
  '& .row-glow': {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '100%',
    height: '100%',
    background: 'radial-gradient(ellipse at center, rgba(0, 255, 136, 0.1) 0%, transparent 70%)',
    opacity: 0,
    transition: 'opacity 0.3s ease',
    pointerEvents: 'none',
  }
}));

const PremiumActionButton = styled(IconButton)(({ theme, variant = 'primary' }) => {
  const colors = {
    primary: '#00ff88',
    secondary: '#0088ff',
    danger: '#ff4444',
    warning: '#ff8800',
  };
  
  const color = colors[variant] || colors.primary;
  
  return {
    opacity: 0.7,
    transform: 'translateX(10px)',
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
    background: `linear-gradient(135deg, ${alpha(color, 0.1)} 0%, ${alpha(color, 0.05)} 100%)`,
    border: `1px solid ${alpha(color, 0.2)}`,
    borderRadius: '12px',
    color: color,
    width: '36px',
    height: '36px',
    '&:hover': {
      background: `linear-gradient(135deg, ${alpha(color, 0.2)} 0%, ${alpha(color, 0.1)} 100%)`,
      boxShadow: `0 0 20px ${alpha(color, 0.4)}, 0 4px 15px ${alpha(color, 0.2)}`,
      transform: 'translateX(0) scale(1.1)',
      borderColor: alpha(color, 0.4),
      opacity: 1,
    },
    '&:active': {
      transform: 'translateX(0) scale(0.95)',
    }
  };
});

function ValidationKPIs({ refresh }) {
  const [validationData, setValidationData] = useState([]);
  const [reasonsData, setReasonsData] = useState([]);
  const [penaltyMetrics, setPenaltyMetrics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedPO, setSelectedPO] = useState("");
  const [tabValue, setTabValue] = useState(0);
  const theme = useTheme();

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Helper function to parse CSV
  const parseCSV = (csv) => {
    const [header, ...rows] = csv.trim().split(/\r?\n/);
    const keys = header.split(",");
    return rows.map(row => {
      const values = row.split(/,(?=(?:[^"]*"[^"]*")*[^"]*$)/).map(v => v.replace(/^"|"$/g, ""));
      const obj = {};
      keys.forEach((k, i) => obj[k] = values[i]);
      return obj;
    });
  };

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
        // Fetch validation results
        const validationRes = await fetch(`${BACKEND_URL}/results/final_reason_validation_results.csv`);
        const validationText = await validationRes.text();
        
        // Try to fetch from the new endpoint first
        let reasonsParsed = [];
        try {
          const allPenaltiesRes = await fetch(`${BACKEND_URL}/all-penalties`);
          if (allPenaltiesRes.ok) {
            const allPenaltiesData = await allPenaltiesRes.json();
            reasonsParsed = allPenaltiesData.penalties;
          } else {
            // Fallback to direct CSV file
            const reasonsRes = await fetch(`${BACKEND_URL}/results/reasons.csv`);
            const reasonsText = await reasonsRes.text();
            reasonsParsed = parseCSV(reasonsText);
          }
        } catch (error) {
          console.error("Error fetching penalties:", error);
          const reasonsRes = await fetch(`${BACKEND_URL}/results/reasons.csv`);
          const reasonsText = await reasonsRes.text();
          reasonsParsed = parseCSV(reasonsText);
        }
        
        // Parse validation data
        const validationParsed = parseCSV(validationText);
        
        // Make sure penalty values are treated as numbers
        const reasonsWithNumberPenalty = reasonsParsed.map(row => ({
          ...row,
          penalty: row.penalty ? parseFloat(row.penalty) : 0,
          reason_normalized: row.reason ? row.reason.trim().toLowerCase() : ''
        }));
        
        // Enhance validation data with normalized stated_reason
        const enhancedValidationData = validationParsed.map(row => ({
          ...row,
          stated_reason_normalized: row.stated_reason ? row.stated_reason.trim().toLowerCase() : ''
        }));
        
        setValidationData(enhancedValidationData);
        setReasonsData(reasonsWithNumberPenalty);
        
        // Fetch penalty metrics
        const penaltyRes = await fetch(`${BACKEND_URL}/calculate-penalties`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' }
        });
        const penaltyData = await penaltyRes.json();
        
        if (penaltyData.status === 'success') {
          setPenaltyMetrics(penaltyData.metrics);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, [refresh]);

  // Calculate metrics only after data is loaded
  const metrics = React.useMemo(() => {
    if (!validationData.length) {
      return {
        total: 0,
        match: 0,
        notMatch: 0,
        matchDist: [],
        reasonDist: [],
        topReasons: [],
        totalPenaltyAmount: 0,
        saveablePenaltyAmount: 0
      };
    }

    const total = validationData.length;
    let match = 0, notMatch = 0;
    const reasonCounts = {};
    
    validationData.forEach((row) => {
      if (row["Match/Not"] === "True") match++;
      else if (row["Match/Not"] === "False") notMatch++;
      if (row.stated_reason) {
        const reason = row.stated_reason.trim();
        if (reason) reasonCounts[reason] = (reasonCounts[reason] || 0) + 1;
      }
    });
    
    const matchDist = [
      { name: "Match", value: match },
      { name: "Not Match", value: notMatch },
    ];
    
    const reasonDist = Object.entries(reasonCounts).map(([name, value]) => ({ name, value }));
    const filteredReasonDist = reasonDist.filter(r => r.name.trim().toLowerCase() !== "no reason");
    const topReasons = filteredReasonDist.slice().sort((a, b) => b.value - a.value).slice(0, 5);
    
    // Calculate penalty metrics
    const totalPenaltyAmount = penaltyMetrics?.total_penalty_claimed || 
      reasonsData.reduce((sum, row) => {
        const penalty = parseFloat(row.penalty || 0);
        return isNaN(penalty) ? sum : sum + penalty;
      }, 0);
      
    const saveablePenaltyAmount = penaltyMetrics?.penalty_that_can_be_saved ||
      validationData
        .filter(vRow => vRow["Match/Not"] === "False")
        .map(vRow => {
          const reasonRow = reasonsData.find(
            r => r.PO_ID === vRow.PO_ID && 
                 r.reason && vRow.stated_reason &&
                 r.reason.trim().toLowerCase() === vRow.stated_reason.trim().toLowerCase()
          );
          return reasonRow ? parseFloat(reasonRow.penalty || 0) : 0;
        })
        .reduce((sum, penalty) => sum + (isNaN(penalty) ? 0 : penalty), 0);

    return {
      total,
      match,
      notMatch,
      matchDist,
      reasonDist,
      topReasons,
      totalPenaltyAmount,
      saveablePenaltyAmount
    };
  }, [validationData, reasonsData, penaltyMetrics]);

  // PO options for filtering
  const poOptions = React.useMemo(() => {
    return validationData.map(row => row.PO_ID);
  }, [validationData]);

  const filteredRows = selectedPO
    ? validationData.filter(row => row.PO_ID === selectedPO)
    : validationData;

  if (loading) {
    return (
      <Box sx={{ p: 3 }}>
        <Typography variant="h5" align="center" sx={{ mb: 4, fontWeight: 700, color: '#00e6e6' }}>
          Discrepancy Validator
        </Typography>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Grid container spacing={3}>
              {[1, 2, 3, 4].map((i) => (
                <Grid item xs={12} md={3} key={i}>
                  <KPICard isLoading />
                </Grid>
              ))}
            </Grid>
          </Grid>
          <Grid item xs={12} md={6}>
            <ChartCard isLoading />
          </Grid>
          <Grid item xs={12} md={6}>
            <ChartCard isLoading />
          </Grid>
        </Grid>
      </Box>
    );
  }

  return (
    <Box sx={{ 
      p: { xs: 2, sm: 3, md: 4 },
      background: `
        linear-gradient(135deg, 
          rgba(10, 14, 26, 0.95) 0%, 
          rgba(20, 24, 36, 0.98) 25%,
          rgba(15, 19, 31, 0.96) 50%,
          rgba(25, 29, 41, 0.98) 75%,
          rgba(10, 14, 26, 0.95) 100%
        ),
        radial-gradient(circle at 20% 20%, rgba(0, 255, 136, 0.05) 0%, transparent 50%),
        radial-gradient(circle at 80% 80%, rgba(0, 136, 255, 0.05) 0%, transparent 50%),
        radial-gradient(circle at 40% 60%, rgba(255, 0, 128, 0.03) 0%, transparent 50%)
      `,
      minHeight: '100vh',
      position: 'relative',
      overflow: 'hidden',
      '&::before': {
        content: '""',
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        background: `
          linear-gradient(45deg, transparent 30%, rgba(0, 255, 136, 0.01) 50%, transparent 70%),
          linear-gradient(-45deg, transparent 30%, rgba(0, 136, 255, 0.01) 50%, transparent 70%)
        `,
        animation: `${slideUp} 20s linear infinite`,
        zIndex: 0,
        pointerEvents: 'none',
      }
    }}>
      {/* Premium Header */}
      <Box sx={{ textAlign: 'center', mb: 6, position: 'relative' }}>
        {/* Animated background */}
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: '200%',
            height: '200%',
            background: 'radial-gradient(ellipse at center, rgba(0, 255, 136, 0.03) 0%, transparent 70%)',
            animation: `${pulse} 4s ease-in-out infinite`,
            zIndex: 0,
          }}
        />
        
        <Box sx={{ position: 'relative', zIndex: 1 }}>
          <GradientText 
            variant="h3" 
            gradient="linear-gradient(135deg, #00ff88 0%, #00ccff 50%, #0088ff 100%)"
            sx={{ 
              fontWeight: 900,
              mb: 2,
              fontSize: { xs: '2rem', sm: '2.5rem', md: '3rem' },
              textShadow: '0 0 30px rgba(0, 255, 136, 0.5), 0 0 60px rgba(0, 204, 255, 0.3)',
              animation: `${glow} 2s ease-in-out infinite alternate`,
            }}
          >
            ⚡ Discrepancy Validator
          </GradientText>
          
          <Typography 
            variant="h6" 
            sx={{ 
              color: alpha('#ffffff', 0.8),
              fontWeight: 500,
              mb: 2,
              fontSize: { xs: '1rem', sm: '1.1rem' },
              letterSpacing: '1px',
              textTransform: 'uppercase',
            }}
          >
            Premium AI-Powered Analytics Dashboard
          </Typography>
          
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, flexWrap: 'wrap' }}>
            {['Real-time', 'ML Insights', 'Predictive', 'Advanced'].map((tag, index) => (
              <Chip
                key={tag}
                label={tag}
                size="small"
                sx={{
                  background: `linear-gradient(135deg, ${alpha(NEON_COLORS[index], 0.2)}, ${alpha(NEON_COLORS[index], 0.1)})`,
                  color: NEON_COLORS[index],
                  border: `1px solid ${alpha(NEON_COLORS[index], 0.3)}`,
                  fontWeight: 600,
                  fontSize: '0.75rem',
                  animation: `${float} ${2.5 + index * 0.5}s ease-in-out infinite`,
                  '&:hover': {
                    boxShadow: `0 0 20px ${alpha(NEON_COLORS[index], 0.4)}`,
                    transform: 'scale(1.1)',
                  }
                }}
              />
            ))}
          </Box>
        </Box>
      </Box>

      <Grid container spacing={3}>
        {/* KPI Cards */}
        <Grid item xs={12}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={3}>
              <PremiumKPICard
                title="Total Validations"
                value={metrics.total}
                subtitle="Financial Transactions Analyzed"
                icon={AssessmentIcon}
                color="#00ff88"
                trend="+12.5%"
                trendDirection="up"
                isLoading={loading}
                delay={0}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <PremiumKPICard
                title="Match Accuracy"
                value={`${metrics.total ? ((metrics.match / metrics.total) * 100).toFixed(1) : 0}%`}
                subtitle="Validated PO to Invoice Alignment"
                icon={CheckIcon}
                color="#00ccff"
                trend="+2.3%"
                trendDirection="up"
                isLoading={loading}
                delay={1}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <PremiumKPICard
                title="Discrepancies Found"
                value={metrics.notMatch}
                subtitle="Identified for Resolution"
                icon={ErrorIcon}
                color="#ff4444"
                trend="-5.1%"
                trendDirection="down"
                isLoading={loading}
                delay={2}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <PremiumKPICard
                title="Top Issue"
                value={metrics.topReasons.length > 0 ? 
                  (metrics.topReasons[0].name.length > 15 
                    ? metrics.topReasons[0].name.substring(0, 13) + '...' 
                    : metrics.topReasons[0].name) : '-'}
                subtitle={metrics.topReasons.length > 0 ? `${metrics.topReasons[0].value} Occurrences` : 'No data'}
                icon={ShowChartIcon}
                color="#8800ff"
                trend="+7.8%"
                trendDirection="up"
                isLoading={loading}
                delay={3}
              />
            </Grid>
          </Grid>
        </Grid>
        
        {/* Charts Row */}
        <Grid item xs={12} md={6}>
          <PremiumChartCard
            title="Average Penalty by Reason"
            subtitle="Financial Impact Analysis"
            isLoading={loading}
            delay={0}
            icon={ShowChartIcon}
            actions={[
              <Tooltip title="Refresh Data">
                <RefreshIcon />
              </Tooltip>
            ]}
          >
            <Box sx={{ height: 350, display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={metrics.topReasons.slice(0, 5).map((r, index) => {
                      const penaltyAmounts = reasonsData
                        .filter(item => 
                          item.reason && r.name && 
                          item.reason.trim().toLowerCase() === r.name.trim().toLowerCase()
                        )
                        .map(item => parseFloat(item.penalty || 0))
                        .filter(p => !isNaN(p));
                      
                      const avgPenalty = penaltyAmounts.length > 0 
                        ? penaltyAmounts.reduce((sum, p) => sum + p, 0) / penaltyAmounts.length 
                        : 0;
                      
                      return {
                        name: r.name.length > 20 ? r.name.substring(0, 18) + '...' : r.name,
                        value: parseFloat(avgPenalty.toFixed(2)),
                        fullName: r.name,
                        count: penaltyAmounts.length,
                        total: penaltyAmounts.reduce((sum, p) => sum + p, 0)
                      };
                    }).filter(item => item.value > 0)}
                    cx="50%"
                    cy="50%"
                    outerRadius={120}
                    innerRadius={60}
                    paddingAngle={3}
                    dataKey="value"
                  >
                    {metrics.topReasons.slice(0, 5).map((_, index) => (
                      <Cell 
                        key={`cell-${index}`} 
                        fill={COLORS[index % COLORS.length]}
                        stroke="rgba(255,255,255,0.1)"
                        strokeWidth={2}
                      />
                    ))}
                  </Pie>
                  <RechartsTooltip
                    contentStyle={{
                      backgroundColor: 'rgba(35, 39, 51, 0.95)',
                      border: '1px solid rgba(255, 255, 255, 0.1)',
                      borderRadius: '8px',
                      color: '#e3eafc'
                    }}
                    formatter={(value, name, props) => [
                      `$${value.toLocaleString()}`, 
                      'Average Penalty'
                    ]}
                    labelFormatter={(_, payload) => payload?.[0]?.payload?.fullName || ''}
                  />
                  <Legend 
                    verticalAlign="bottom"
                    height={36}
                    iconType="circle"
                    wrapperStyle={{ 
                      color: '#e3eafc', 
                      fontSize: '14px',
                      paddingTop: '20px'
                    }}
                  />
                </PieChart>
              </ResponsiveContainer>
            </Box>
          </PremiumChartCard>
        </Grid>
        
        <Grid item xs={12} md={6}>
          <PremiumChartCard
            title="Top Discrepancy Drivers"
            subtitle="Key Validation Challenges"
            isLoading={loading}
            delay={1}
            icon={TrendingUpIcon}
            actions={[
              <Tooltip title="Filter Data">
                <FilterIcon />
              </Tooltip>
            ]}
          >
            <ResponsiveContainer width="100%" height={350}>
              <BarChart 
                data={metrics.topReasons.map(r => ({
                  name: r.name.length > 20 ? r.name.substring(0, 18) + '...' : r.name,
                  value: r.value,
                  percentage: ((r.value / metrics.total) * 100).toFixed(1),
                  fullName: r.name
                }))} 
                layout="vertical" 
                margin={{ left: 30, right: 30, top: 20, bottom: 20 }}
              >
                <XAxis 
                  type="number" 
                  allowDecimals={false}
                  axisLine={false}
                  tickLine={false}
                  tick={{ fill: '#78909c', fontSize: 12 }}
                />
                <YAxis 
                  dataKey="name" 
                  type="category" 
                  axisLine={false}
                  tickLine={false}
                  tick={{ fill: '#e3eafc', fontSize: 13, fontWeight: 500 }} 
                  width={150}
                />
                <Bar 
                  dataKey="value" 
                  fill="url(#barGradient)"
                  barSize={30}
                  radius={[0, 6, 6, 0]}
                />
                <RechartsTooltip
                  contentStyle={{
                    backgroundColor: 'rgba(35, 39, 51, 0.95)',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    borderRadius: '8px',
                    color: '#e3eafc'
                  }}
                  formatter={(value, name, props) => [
                    `${value} occurrences (${props.payload.percentage}%)`, 
                    'Frequency'
                  ]}
                  labelFormatter={(label, payload) => payload?.[0]?.payload?.fullName || label}
                />
                <defs>
                  <linearGradient id="barGradient" x1="0" y1="0" x2="1" y2="0">
                    <stop offset="0%" stopColor="#1976d2" />
                    <stop offset="100%" stopColor="#00e6e6" />
                  </linearGradient>
                </defs>
              </BarChart>
            </ResponsiveContainer>
          </PremiumChartCard>
        </Grid>
        
        {/* Premium Table */}
        <Grid item xs={12}>
          <PremiumChartCard
            title="Validation Insights Overview"
            subtitle="Detailed Analysis & Metrics"
            isLoading={loading}
            delay={2}
            icon={AssessmentIcon}
            height={500}
          >
            <PremiumTableContainer
              sx={{ 
                maxHeight: 400,
                '&::-webkit-scrollbar': {
                  width: '12px',
                  height: '12px',
                },
                '&::-webkit-scrollbar-track': {
                  background: 'rgba(30, 34, 46, 0.5)',
                  borderRadius: '6px',
                },
                '&::-webkit-scrollbar-thumb': {
                  background: 'linear-gradient(135deg, #00ff88, #00ccff)',
                  borderRadius: '6px',
                  '&:hover': {
                    background: 'linear-gradient(135deg, #00ccff, #0088ff)',
                  },
                },
                '&::-webkit-scrollbar-corner': {
                  background: 'rgba(30, 34, 46, 0.5)',
                },
              }}
            >
              <Table size="small" stickyHeader>
                <TableHead>
                  <PremiumTableRow>
                    {['Reason', 'Count', 'Accuracy', 'Avg Penalty', 'Total Impact'].map((header) => (
                      <PremiumTableCell 
                        key={header}
                        isHeader={true}
                      >
                        {header}
                      </PremiumTableCell>
                    ))}
                  </PremiumTableRow>
                </TableHead>
                <TableBody>
                  {metrics.topReasons.slice(0, 8).map((r, index) => {
                    const matchCount = validationData.filter(row => 
                      row.stated_reason && 
                      row.stated_reason.trim().toLowerCase() === r.name.trim().toLowerCase() && 
                      row["Match/Not"] === "True"
                    ).length;
                    
                    const notMatchCount = validationData.filter(row => 
                      row.stated_reason && 
                      row.stated_reason.trim().toLowerCase() === r.name.trim().toLowerCase() && 
                      row["Match/Not"] === "False"
                    ).length;
                    
                    const totalCount = matchCount + notMatchCount;
                    const matchRate = totalCount ? (matchCount / totalCount) * 100 : 0;
                    
                    const penaltyAmounts = reasonsData
                      .filter(item => 
                        item.reason && r.name && 
                        item.reason.trim().toLowerCase() === r.name.trim().toLowerCase()
                      )
                      .map(item => parseFloat(item.penalty || 0))
                      .filter(p => !isNaN(p));
                    
                    const totalPenalty = penaltyAmounts.reduce((sum, p) => sum + p, 0);
                    const avgPenalty = penaltyAmounts.length > 0 ? totalPenalty / penaltyAmounts.length : 0;
                    
                    return (
                      <PremiumTableRow 
                        key={r.name} 
                        isClickable={true}
                      >
                        <Box className="row-glow" />
                        <PremiumTableCell sx={{ 
                          maxWidth: '200px',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          whiteSpace: 'nowrap'
                        }}>
                          <Tooltip title={r.name}>
                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                              <Box
                                sx={{
                                  width: 8,
                                  height: 8,
                                  borderRadius: '50%',
                                  background: `linear-gradient(135deg, ${NEON_COLORS[index % NEON_COLORS.length]}, ${alpha(NEON_COLORS[index % NEON_COLORS.length], 0.6)})`,
                                  boxShadow: `0 0 10px ${alpha(NEON_COLORS[index % NEON_COLORS.length], 0.6)}`,
                                }}
                              />
                              <span>{r.name}</span>
                            </Box>
                          </Tooltip>
                        </PremiumTableCell>
                        <PremiumTableCell sx={{ textAlign: 'center' }}>
                          <Chip 
                            label={totalCount}
                            size="small"
                            sx={{ 
                              background: `linear-gradient(135deg, ${alpha('#00ccff', 0.2)}, ${alpha('#0088ff', 0.3)})`,
                              color: '#00ccff',
                              fontWeight: 700,
                              border: `1px solid ${alpha('#00ccff', 0.3)}`,
                              boxShadow: `0 0 15px ${alpha('#00ccff', 0.2)}`,
                            }}
                          />
                        </PremiumTableCell>
                        <PremiumTableCell sx={{ textAlign: 'center' }}>
                          <Chip 
                            label={`${matchRate.toFixed(1)}%`}
                            size="small"
                            sx={{ 
                              background: `linear-gradient(135deg, ${alpha(
                                matchRate < 70 ? '#ff4444' : matchRate < 90 ? '#ff8800' : '#00ff88', 
                                0.2
                              )}, ${alpha(
                                matchRate < 70 ? '#ff4444' : matchRate < 90 ? '#ff8800' : '#00ff88', 
                                0.3
                              )})`,
                              color: matchRate < 70 ? '#ff4444' : matchRate < 90 ? '#ff8800' : '#00ff88',
                              fontWeight: 700,
                              border: `1px solid ${alpha(
                                matchRate < 70 ? '#ff4444' : matchRate < 90 ? '#ff8800' : '#00ff88', 
                                0.4
                              )}`,
                              boxShadow: `0 0 15px ${alpha(
                                matchRate < 70 ? '#ff4444' : matchRate < 90 ? '#ff8800' : '#00ff88', 
                                0.2
                              )}`,
                            }}
                          />
                        </PremiumTableCell>
                        <PremiumTableCell sx={{ textAlign: 'right', fontWeight: 600 }}>
                          <GradientText 
                            variant="body2"
                            gradient="linear-gradient(135deg, #00ccff 0%, #0088ff 100%)"
                          >
                            ${avgPenalty.toFixed(2)}
                          </GradientText>
                        </PremiumTableCell>
                        <PremiumTableCell sx={{ textAlign: 'right' }}>
                          <GradientText 
                            variant="body1"
                            gradient="linear-gradient(135deg, #00ff88 0%, #00ccff 100%)"
                            sx={{ fontWeight: 800, fontSize: '1rem' }}
                          >
                            ${totalPenalty.toLocaleString()}
                          </GradientText>
                        </PremiumTableCell>
                      </PremiumTableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </PremiumTableContainer>
          </PremiumChartCard>
        </Grid>
        
        {/* Enhanced Tabs Section */}
        <Grid item xs={12}>
          <Card sx={{ 
            background: 'linear-gradient(145deg, rgba(35, 39, 51, 0.8) 0%, rgba(25, 28, 36, 0.9) 100%)',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            borderRadius: '16px',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.37)'
          }}>
            <Box sx={{ borderBottom: 1, borderColor: 'rgba(255, 255, 255, 0.1)' }}>
              <Tabs 
                value={tabValue} 
                onChange={handleTabChange}
                sx={{
                  '& .MuiTab-root': {
                    color: '#78909c',
                    fontWeight: 600,
                    textTransform: 'none',
                    fontSize: '1rem',
                    '&.Mui-selected': {
                      color: '#00e6e6'
                    }
                  },
                  '& .MuiTabs-indicator': {
                    backgroundColor: '#00e6e6',
                    height: '3px',
                    borderRadius: '2px'
                  }
                }}
              >
                <Tab label="Detailed Analysis" />
                <Tab label="Penalty Breakdown" />
              </Tabs>
            </Box>
            
            <CardContent sx={{ p: 3 }}>
              {tabValue === 0 && (
                <Fade in timeout={300}>
                  <Box>
                    <FormControl fullWidth sx={{ mb: 3 }}>
                      <InputLabel 
                        sx={{ color: '#78909c' }}
                        id="po-select-label"
                      >
                        Filter by Purchase Order
                      </InputLabel>
                      <Select
                        labelId="po-select-label"
                        value={selectedPO}
                        label="Filter by Purchase Order"
                        onChange={e => setSelectedPO(e.target.value)}
                        sx={{
                          '& .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(255, 255, 255, 0.2)'
                          },
                          '&:hover .MuiOutlinedInput-notchedOutline': {
                            borderColor: '#00e6e6'
                          },
                          '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                            borderColor: '#00e6e6'
                          },
                          '& .MuiSelect-select': {
                            color: '#e3eafc'
                          }
                        }}
                      >
                        <MenuItem value="">All Purchase Orders</MenuItem>
                        {poOptions.map((po, index) => (
                          <MenuItem key={index} value={po}>{po}</MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                    
                    <TableContainer 
                      component={Paper}
                      sx={{ 
                        backgroundColor: 'transparent',
                        boxShadow: 'none',
                        border: '1px solid rgba(255, 255, 255, 0.1)',
                        borderRadius: '12px',
                        maxHeight: 400
                      }}
                    >
                      <Table size="small" stickyHeader>
                        <TableHead>
                          <TableRow>
                            {['PO ID', 'Reason', 'Status', 'Comments'].map((header) => (
                              <TableCell 
                                key={header}
                                sx={{ 
                                  backgroundColor: 'rgba(35, 39, 51, 0.9)',
                                  color: '#00e6e6',
                                  fontWeight: 700,
                                  borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
                                }}
                              >
                                {header}
                              </TableCell>
                            ))}
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {filteredRows.slice(0, 50).map((row, idx) => (
                            <TableRow 
                              key={idx}
                              sx={{ 
                                '&:hover': { 
                                  backgroundColor: 'rgba(0, 230, 230, 0.05)' 
                                }
                              }}
                            >
                              <TableCell sx={{ color: '#e3eafc', fontWeight: 500 }}>
                                {row.PO_ID}
                              </TableCell>
                              <TableCell sx={{ color: '#b0bec5' }}>
                                {row.stated_reason}
                              </TableCell>
                              <TableCell>
                                <Chip 
                                  label={row["Match/Not"] === "True" ? "Match" : "No Match"}
                                  size="small"
                                  sx={{ 
                                    backgroundColor: alpha(
                                      row["Match/Not"] === "True" ? '#4caf50' : '#f44336', 
                                      0.2
                                    ),
                                    color: row["Match/Not"] === "True" ? '#4caf50' : '#f44336',
                                    fontWeight: 600
                                  }}
                                />
                              </TableCell>
                              <TableCell sx={{ 
                                color: '#78909c',
                                maxWidth: '300px',
                                overflow: 'hidden',
                                textOverflow: 'ellipsis',
                                whiteSpace: 'nowrap'
                              }}>
                                {row.Comments || '-'}
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </Box>
                </Fade>
              )}
              
              {tabValue === 1 && (
                <Fade in timeout={300}>
                  <Box>
                    <PenaltyTable validationData={validationData} reasonsData={reasonsData} />
                  </Box>
                </Fade>
              )}
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
}

export default ValidationKPIs;
