import React, { useEffect, useState } from "react";
import { Box, Grid, Typography, CircularProgress, FormControl, InputLabel, Select, MenuItem, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Card, CardContent, Tabs, Tab } from "@mui/material";
import { PieChart, Pie, Cell, Tooltip, Legend, BarChart, Bar, XAxis, YAxis, ResponsiveContainer, LineChart, Line } from "recharts";
import { BACKEND_URL } from "../utils/backend";
import PenaltyTable from "./PenaltyTable";

const COLORS = ["#1976d2", "#43a047", "#fbc02d", "#e53935", "#8e24aa", "#00acc1"];

function ValidationKPIs({ refresh }) {
  const [validationData, setValidationData] = useState([]);
  const [reasonsData, setReasonsData] = useState([]);
  const [penaltyMetrics, setPenaltyMetrics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedPO, setSelectedPO] = useState("");
  const [tabValue, setTabValue] = useState(0);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
        // Fetch validation results
        const validationRes = await fetch(`${BACKEND_URL}/results/final_reason_validation_results.csv`);
        const validationText = await validationRes.text();
        
        // Fetch reasons with penalties - read directly from the updated reasons.csv
        const reasonsRes = await fetch(`${BACKEND_URL}/results/reasons.csv`);
        const reasonsText = await reasonsRes.text();
        
        // Parse CSV
        const parseCSV = (csv) => {
          const [header, ...rows] = csv.trim().split(/\r?\n/);
          const keys = header.split(",");
          return rows.map(row => {
            const values = row.split(/,(?=(?:[^"]*"[^"]*")*[^"]*$)/).map(v => v.replace(/^"|"$/g, ""));
            const obj = {};
            keys.forEach((k, i) => obj[k] = values[i]);
            return obj;
          });
        };
        
        const validationParsed = parseCSV(validationText);
        const reasonsParsed = parseCSV(reasonsText);
        
        // Make sure penalty values are treated as numbers
        const reasonsWithNumberPenalty = reasonsParsed.map(row => ({
          ...row,
          penalty: row.penalty ? parseFloat(row.penalty) : 0
        }));
        
        setValidationData(validationParsed);
        setReasonsData(reasonsWithNumberPenalty);
        
        // Fetch penalty metrics - this endpoint will use the existing penalty values
        const penaltyRes = await fetch(`${BACKEND_URL}/calculate-penalties`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' }
        });
        const penaltyData = await penaltyRes.json();
        
        if (penaltyData.status === 'success') {
          setPenaltyMetrics(penaltyData.metrics);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, [refresh]);

  if (loading) return <CircularProgress />;

  // KPI: Total Validations, Match/Not, Distribution
  const total = validationData.length;
  let match = 0, notMatch = 0;
  const reasonCounts = {};
  validationData.forEach((row) => {
    if (row["Match/Not"] === "True") match++;
    else if (row["Match/Not"] === "False") notMatch++;
    if (row.stated_reason) {
      const reason = row.stated_reason.trim();
      if (reason) reasonCounts[reason] = (reasonCounts[reason] || 0) + 1;
    }
  });
  const matchDist = [
    { name: "Match", value: match },
    { name: "Not Match", value: notMatch },
  ];
  const reasonDist = Object.entries(reasonCounts).map(([name, value]) => ({ name, value }));

  // Show only top 5 reasons for better visibility
  const topReasons = reasonDist.slice().sort((a, b) => b.value - a.value).slice(0, 5);

  // PO slicer
  const poOptions = validationData.map(row => row.PO_ID);
  const filteredRows = selectedPO
    ? validationData.filter(row => row.PO_ID === selectedPO)
    : validationData;

  return (
    <Box>
  <Typography variant="h5" align="center" sx={{ mb: 3, fontWeight: 700 }}>Discrepancy Validator</Typography>
      <Grid container spacing={2}>
        {/* KPI Row at top - Revamped */}
        <Grid item xs={12}>
          <Card sx={{ mb: 2, boxShadow: '0 4px 12px rgba(0,0,0,0.08)' }}>
            <CardContent>
              <Typography variant="h6" align="center" sx={{ mb: 2, color: '#1565c0', fontWeight: 600 }}>
                Supply Chain Validation Metrics
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={6} md={3}>
                  <Card sx={{ boxShadow: 'none', bgcolor: '#f3f8ff', height: '100%', border: '1px solid #e0e0e0' }}>
                    <CardContent sx={{ textAlign: 'center', p: 2 }}>
                      <Typography variant="subtitle2" fontWeight={500} color="text.secondary">
                        Total Validations
                      </Typography>
                      <Typography variant="h4" color="#1565c0" fontWeight={700} sx={{ mt: 1 }}>
                        {total.toLocaleString()}
                      </Typography>
                      <Typography variant="body2" color="text.secondary" sx={{ mt: 1, fontSize: '0.75rem' }}>
                        Supply Chain Transactions Processed
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid item xs={6} md={3}>
                  <Card sx={{ boxShadow: 'none', bgcolor: '#f4f8f3', height: '100%', border: '1px solid #e0e0e0' }}>
                    <CardContent sx={{ textAlign: 'center', p: 2 }}>
                      <Typography variant="subtitle2" fontWeight={500} color="text.secondary">
                        Match Accuracy
                      </Typography>
                      <Typography variant="h4" color="#2e7d32" fontWeight={700} sx={{ mt: 1 }}>
                        {total ? ((match / total) * 100).toFixed(1) : 0}%
                      </Typography>
                      <Typography variant="body2" color="text.secondary" sx={{ mt: 1, fontSize: '0.75rem' }}>
                        Validated PO to Invoice Alignment
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid item xs={6} md={3}>
                  <Card sx={{ boxShadow: 'none', bgcolor: '#fef7f7', height: '100%', border: '1px solid #e0e0e0' }}>
                    <CardContent sx={{ textAlign: 'center', p: 2 }}>
                      <Typography variant="subtitle2" fontWeight={500} color="text.secondary">
                        POs with Discrepancy
                      </Typography>
                      <Typography variant="h4" color="#c62828" fontWeight={700} sx={{ mt: 1 }}>
                        {notMatch.toLocaleString()}
                      </Typography>
                      <Typography variant="body2" color="text.secondary" sx={{ mt: 1, fontSize: '0.75rem' }}>
                        Identified for Resolution
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid item xs={6} md={3}>
                  <Card sx={{ boxShadow: 'none', bgcolor: '#f7f7ff', height: '100%', border: '1px solid #e0e0e0' }}>
                    <CardContent sx={{ textAlign: 'center', p: 2 }}>
                      <Typography variant="subtitle2" fontWeight={500} color="text.secondary">
                        Top Discrepancy Reason
                      </Typography>
                      <Typography variant="h6" color="#5c6bc0" fontWeight={600} sx={{ mt: 1, fontSize: '1.1rem' }}>
                        {topReasons.length > 0 ? (
                          topReasons[0].name.length > 20 
                            ? topReasons[0].name.substring(0, 18) + '...' 
                            : topReasons[0].name
                        ) : '-'}
                      </Typography>
                      <Typography variant="body2" color="text.secondary" sx={{ mt: 1, fontSize: '0.75rem' }}>
                        {topReasons.length > 0 ? `${topReasons[0].value} Occurrences` : '-'}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
        
        {/* Penalty Metrics Card - Revamped */}
        <Grid item xs={12}>
          <Card sx={{ mb: 2, boxShadow: '0 4px 12px rgba(0,0,0,0.08)' }}>
            <CardContent>
              <Typography variant="h6" align="center" sx={{ mb: 2, color: '#1565c0', fontWeight: 600 }}>
                Financial Impact Analysis
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={12} md={4}>
                  <Card sx={{ 
                    boxShadow: 'none', 
                    height: '100%', 
                    borderRadius: '8px',
                    background: 'linear-gradient(135deg, #e3f2fd 0%, #bbdefb 100%)',
                    border: '1px solid #90caf9'
                  }}>
                    <CardContent sx={{ textAlign: 'center', p: 3 }}>
                      <Typography variant="subtitle1" fontWeight={600} color="#0d47a1">
                        Total Penalty Exposure
                      </Typography>
                      <Typography variant="h3" color="#1565c0" fontWeight={800} sx={{ mt: 2 }}>
                        ${penaltyMetrics?.total_penalty_claimed?.toLocaleString() || '0'}
                      </Typography>
                      <Typography variant="body2" sx={{ mt: 1, color: '#1565c0' }}>
                        Across {validationData.length} validated transactions
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid item xs={12} md={4}>
                  <Card sx={{ 
                    boxShadow: 'none', 
                    height: '100%', 
                    borderRadius: '8px',
                    background: 'linear-gradient(135deg, #e8f5e9 0%, #c8e6c9 100%)',
                    border: '1px solid #a5d6a7'
                  }}>
                    <CardContent sx={{ textAlign: 'center', p: 3 }}>
                      <Typography variant="subtitle1" fontWeight={600} color="#2e7d32">
                        Potential Cost Recovery
                      </Typography>
                      <Typography variant="h3" color="#2e7d32" fontWeight={800} sx={{ mt: 2 }}>
                        ${penaltyMetrics?.penalty_that_can_be_saved?.toLocaleString() || '0'}
                      </Typography>
                      <Typography variant="body2" sx={{ mt: 1, color: '#2e7d32' }}>
                        From {notMatch} disputable transactions
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid item xs={12} md={4}>
                  <Card sx={{ 
                    boxShadow: 'none', 
                    height: '100%', 
                    borderRadius: '8px',
                    background: 'linear-gradient(135deg, #fff8e1 0%, #ffecb3 100%)',
                    border: '1px solid #ffe082'
                  }}>
                    <CardContent sx={{ textAlign: 'center', p: 3 }}>
                      <Typography variant="subtitle1" fontWeight={600} color="#e65100">
                        Savings Opportunity Rate
                      </Typography>
                      <Typography variant="h3" color="#e65100" fontWeight={800} sx={{ mt: 2 }}>
                        {penaltyMetrics?.percentage_saveable || '0'}%
                      </Typography>
                      <Typography variant="body2" sx={{ mt: 1, color: '#e65100' }}>
                        ROI on validation process
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
        {/* 100% Stacked Bar Chart: Match/Not Match Proportion by Top 5 Reasons */}
        <Grid item xs={12} md={6}>
          <Card sx={{ mb: 2 }}>
            <CardContent>
              <Typography variant="h6" align="center" mb={2}>Match vs Not Match % by Top 5 Reasons</Typography>
              <ResponsiveContainer width="100%" height={260}>
                <BarChart data={topReasons.map(r => {
                  const matchCount = validationData.filter(row => row.stated_reason && row.stated_reason.trim() === r.name && row["Match/Not"] === "True").length;
                  const notMatchCount = validationData.filter(row => row.stated_reason && row.stated_reason.trim() === r.name && row["Match/Not"] === "False").length;
                  const total = matchCount + notMatchCount;
                  return {
                    reason: r.name,
                    Match: total ? (matchCount / total) * 100 : 0,
                    "Not Match": total ? (notMatchCount / total) * 100 : 0
                  };
                })} margin={{ left: 40, right: 40 }}>
                  <XAxis dataKey="reason" interval={0} tick={{ fontSize: 13, angle: -20, dx: 0, dy: 10, wordBreak: 'break-all' }} />
                  <YAxis allowDecimals={false} domain={[0, 100]} tickFormatter={v => `${v}%`} />
                  <Bar dataKey="Match" stackId="a" fill="#43a047" barSize={24} />
                  <Bar dataKey="Not Match" stackId="a" fill="#e53935" barSize={24} />
                  <Tooltip formatter={v => `${v.toFixed(1)}%`} />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>
        {/* Horizontal Bar Chart: Top 5 Reason Frequency */}
        <Grid item xs={12} md={6}>
          <Card sx={{ mb: 2 }}>
            <CardContent>
              <Typography variant="h6" align="center" mb={2}>Top 5 Reason Frequency</Typography>
              <ResponsiveContainer width="100%" height={260}>
                <BarChart data={topReasons} layout="vertical" margin={{ left: 40, right: 40 }}>
                  <XAxis type="number" allowDecimals={false} />
                  <YAxis dataKey="name" type="category" tick={{ fontWeight: 'bold', fill: '#111', fontSize: 13 }} width={150} />
                  <Bar dataKey="value" fill="#1976d2" barSize={18} />
                  <Tooltip />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>
        
        {/* Penalty by Reason Chart - Fixed and Revamped */}
        <Grid item xs={12} md={12}>
          <Card sx={{ mb: 2, boxShadow: '0 4px 12px rgba(0,0,0,0.08)' }}>
            <CardContent>
              <Typography variant="h6" align="center" mb={2} sx={{ fontWeight: 600, color: '#1565C0' }}>
                Penalty Amount Distribution by Top Reasons ($)
              </Typography>
              <ResponsiveContainer width="100%" height={350}>
                <BarChart 
                  data={topReasons.map(reason => {
                    // Calculate total penalty for this reason
                    const matchingReasons = reasonsData.filter(item => 
                      item.reason && reason.name && 
                      item.reason.trim().toLowerCase() === reason.name.trim().toLowerCase()
                    );
                    
                    const reasonPenalty = matchingReasons.reduce((sum, item) => {
                      const penalty = parseFloat(item.penalty || 0);
                      return isNaN(penalty) ? sum : sum + penalty;
                    }, 0);
                    
                    // Calculate saveable penalty (where Match/Not is False)
                    const saveablePenalty = validationData
                      .filter(vRow => {
                        return vRow.stated_reason && 
                          reason.name && 
                          vRow.stated_reason.trim().toLowerCase() === reason.name.trim().toLowerCase() && 
                          vRow["Match/Not"] === "False";
                      })
                      .map(vRow => {
                        const rRow = reasonsData.find(r => 
                          r.PO_ID === vRow.PO_ID && 
                          r.reason && 
                          vRow.stated_reason && 
                          r.reason.trim().toLowerCase() === vRow.stated_reason.trim().toLowerCase()
                        );
                        const penalty = rRow ? parseFloat(rRow.penalty || 0) : 0;
                        return isNaN(penalty) ? 0 : penalty;
                      })
                      .reduce((sum, penalty) => sum + penalty, 0);
                    
                    // Calculate non-saveable penalty
                    const nonSaveablePenalty = Math.max(0, reasonPenalty - saveablePenalty);
                    
                    // Display shortened reason name for better readability
                    const shortenedName = reason.name.length > 25 
                      ? reason.name.substring(0, 23) + '...' 
                      : reason.name;
                    
                    console.log(`Reason: ${reason.name}, Total: ${reasonPenalty}, Saveable: ${saveablePenalty}`);
                    
                    return {
                      name: shortenedName,
                      fullName: reason.name,
                      totalPenalty: parseFloat(reasonPenalty.toFixed(2)),
                      saveablePenalty: parseFloat(saveablePenalty.toFixed(2)),
                      regularPenalty: parseFloat(nonSaveablePenalty.toFixed(2))
                    };
                  })}
                  margin={{ top: 20, right: 30, left: 30, bottom: 70 }}
                >
                  <XAxis 
                    dataKey="name"
                    interval={0}
                    tick={{ fontSize: 12, angle: -40, textAnchor: 'end' }}
                  />
                  <YAxis 
                    tickFormatter={value => `$${value}`}
                    label={{ value: 'Penalty Amount ($)', angle: -90, position: 'insideLeft', offset: 10 }}
                  />
                  <Tooltip 
                    formatter={(value, name) => [`$${value.toLocaleString()}`, name]}
                    labelFormatter={(label, items) => {
                      if (items.length > 0) {
                        return items[0].payload.fullName;
                      }
                      return label;
                    }}
                  />
                  <Legend 
                    verticalAlign="top" 
                    height={36} 
                    iconSize={15}
                    wrapperStyle={{
                      paddingBottom: '10px',
                      fontWeight: 500
                    }}
                  />
                  <Bar 
                    name="Valid Penalties" 
                    dataKey="regularPenalty" 
                    fill="#2E7D32" 
                    barSize={30} 
                    stackId="a"
                  />
                  <Bar 
                    name="Saveable Penalties" 
                    dataKey="saveablePenalty" 
                    fill="#C62828" 
                    barSize={30} 
                    stackId="a"
                  />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>
        {/* Match vs Not Match Pie Chart */}
        {/* PO-wise Table at the top */}
        <Grid item xs={12} md={12}>
          <Box mt={3}>
            <Typography variant="subtitle2">PO-wise Reasons & Comments</Typography>
            <TableContainer component={Paper} sx={{ maxHeight: 300 }}>
              <Table size="small" stickyHeader>
                <TableHead>
                  <TableRow>
                    <TableCell>PO ID</TableCell>
                    <TableCell>Reason</TableCell>
                    <TableCell>Match/Not</TableCell>
                    <TableCell>Comments</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {filteredRows.map((row, idx) => (
                    <TableRow key={idx}>
                      <TableCell>{row.PO_ID}</TableCell>
                      <TableCell>{row.stated_reason}</TableCell>
                      <TableCell>{row["Match/Not"]}</TableCell>
                      <TableCell>{row.Comments}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Box>
        </Grid>
        <Grid item xs={12} md={12}>
          <Box mt={3}>
            {/* Tabs for switching between filtered view and full penalty analysis */}
            <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
              <Tabs value={tabValue} onChange={handleTabChange} aria-label="validation tabs">
                <Tab label="Filtered View" />
                <Tab label="Penalty Analysis" />
              </Tabs>
            </Box>
            
            {/* Tab Panel 1: Filtered View */}
            {tabValue === 0 && (
              <>
                <FormControl fullWidth>
                  <InputLabel id="po-select-label">Select PO</InputLabel>
                  <Select
                    labelId="po-select-label"
                    value={selectedPO}
                    label="Select PO"
                    onChange={e => setSelectedPO(e.target.value)}
                  >
                    <MenuItem value="">All</MenuItem>
                    {poOptions.map(po => (
                      <MenuItem key={po} value={po}>{po}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <Box mt={2}>
                  <Typography variant="subtitle2">PO-wise Reasons, Comments & Penalties</Typography>
                  <TableContainer component={Paper} sx={{ maxHeight: 300 }}>
                    <Table size="small" stickyHeader>
                      <TableHead>
                        <TableRow>
                          <TableCell>PO ID</TableCell>
                          <TableCell>Reason</TableCell>
                          <TableCell>Match/Not</TableCell>
                          <TableCell>Penalty</TableCell>
                          <TableCell>Comments</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {filteredRows.map((row, idx) => {
                          // Find penalty for this PO and reason from reasonsData
                          const reasonItem = reasonsData.find(
                            item => item.PO_ID === row.PO_ID && item.reason === row.stated_reason
                          );
                          const penaltyAmount = reasonItem ? parseFloat(reasonItem.penalty) : 0;
                          
                          return (
                            <TableRow 
                              key={idx}
                              sx={row["Match/Not"] === "False" ? { bgcolor: 'rgba(255, 235, 238, 0.3)' } : {}}
                            >
                              <TableCell>{row.PO_ID}</TableCell>
                              <TableCell>{row.stated_reason}</TableCell>
                              <TableCell>{row["Match/Not"]}</TableCell>
                              <TableCell>
                                {penaltyAmount > 0 ? (
                                  <Typography 
                                    fontWeight={row["Match/Not"] === "False" ? 700 : 400}
                                    color={row["Match/Not"] === "False" ? "error.main" : "text.primary"}
                                  >
                                    ${penaltyAmount.toFixed(2)}
                                    {row["Match/Not"] === "False" && 
                                      <span style={{ marginLeft: '5px', fontSize: '0.8rem', color: '#d32f2f' }}>
                                        (Saveable)
                                      </span>
                                    }
                                  </Typography>
                                ) : "$0.00"}
                              </TableCell>
                              <TableCell>{row.Comments}</TableCell>
                            </TableRow>
                          );
                        })}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </Box>
              </>
            )}
            
            {/* Tab Panel 2: Penalty Analysis */}
            {tabValue === 1 && (
              <PenaltyTable validationData={validationData} reasonsData={reasonsData} />
            )}
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
}

export default ValidationKPIs;
