import React, { useEffect, useState, useMemo } from "react";
import { 
  Box, 
  Grid, 
  Typography, 
  CircularProgress, 
  FormControl, 
  InputLabel, 
  Select, 
  MenuItem, 
  Paper, 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  Card, 
  CardContent, 
  Tabs, 
  Tab, 
  Chip,
  IconButton,
  Tooltip,
  Skeleton,
  Fade,
  Slide,
  Zoom,
  Grow,
  useTheme,
  alpha,
  LinearProgress,
  Container,
  Stack,
  Avatar,
  Badge,
  Divider
} from "@mui/material";
import { 
  PieChart, 
  Pie, 
  Cell, 
  Tooltip as RechartsTooltip, 
  Legend, 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  ResponsiveContainer, 
  LineChart, 
  Line, 
  CartesianGrid, 
  ComposedChart,
  Area,
  AreaChart,
  RadialBarChart,
  RadialBar,
  Treemap
} from "recharts";
import { 
  AttachMoney as AttachMoneyIcon, 
  ShowChart as ShowChartIcon, 
  MonetizationOn as MonetizationOnIcon, 
  Savings as SavingsIcon,
  TrendingUp as TrendingUpIcon,
  TrendingDown as TrendingDownIcon,
  Assessment as AssessmentIcon,
  ErrorOutline as ErrorIcon,
  CheckCircleOutline as CheckIcon,
  FilterList as FilterIcon,
  Refresh as RefreshIcon,
  Speed as SpeedIcon,
  Timeline as TimelineIcon,
  Analytics as AnalyticsIcon,
  Dashboard as DashboardIcon,
  Insights as InsightsIcon,
  AutoGraph as AutoGraphIcon,
  DonutLarge as DonutLargeIcon,
  DataUsage as DataUsageIcon
} from '@mui/icons-material';
import { keyframes, styled } from '@mui/material/styles';
import { BACKEND_URL } from "../utils/backend";
import PenaltyTable from "./PenaltyTable";



// Premium color palette with neon accents
const NEON_COLORS = [
  "#00ff88", "#ff0080", "#0088ff", "#ff8800", "#8800ff", "#00ffff", "#ff4444", "#44ff44"
];

const GRADIENT_COLORS = [
  "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
  "linear-gradient(135deg, #f093fb 0%, #f5576c 100%)",
  "linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)",
  "linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)",
  "linear-gradient(135deg, #fa709a 0%, #fee140 100%)",
  "linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)",
  "linear-gradient(135deg, #ff9a9e 0%, #fecfef 100%)",
  "linear-gradient(135deg, #a18cd1 0%, #fbc2eb 100%)"
];

// Keyframe animations
const glow = keyframes`
  0% { box-shadow: 0 0 20px rgba(0, 255, 136, 0.3); }
  50% { box-shadow: 0 0 40px rgba(0, 255, 136, 0.6), 0 0 60px rgba(0, 255, 136, 0.4); }
  100% { box-shadow: 0 0 20px rgba(0, 255, 136, 0.3); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const slideUp = keyframes`
  from { opacity: 0; transform: translateY(30px); }
  to { opacity: 1; transform: translateY(0); }
`;

const float = keyframes`
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
`;

// Styled components for premium UI
const GlassmorphicCard = styled(Card)(({ theme, variant = 'primary' }) => ({
  background: variant === 'primary' 
    ? 'linear-gradient(145deg, rgba(15, 23, 42, 0.8) 0%, rgba(30, 41, 59, 0.6) 100%)'
    : 'linear-gradient(145deg, rgba(17, 24, 39, 0.9) 0%, rgba(31, 41, 55, 0.7) 100%)',
  backdropFilter: 'blur(20px)',
  border: '1px solid rgba(255, 255, 255, 0.1)',
  borderRadius: '24px',
  boxShadow: variant === 'primary'
    ? '0 20px 60px rgba(0, 0, 0, 0.5), inset 0 1px 0 rgba(255, 255, 255, 0.1)'
    : '0 25px 80px rgba(0, 0, 0, 0.6), inset 0 1px 0 rgba(255, 255, 255, 0.05)',
  transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
  position: 'relative',
  overflow: 'hidden',
  '&:before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    height: '2px',
    background: 'linear-gradient(90deg, transparent, rgba(0, 255, 136, 0.8), transparent)',
    opacity: 0,
    transition: 'opacity 0.3s ease',
  },
  '&:hover': {
    transform: 'translateY(-8px) scale(1.02)',
    boxShadow: '0 30px 100px rgba(0, 0, 0, 0.7), 0 0 60px rgba(0, 255, 136, 0.1), inset 0 1px 0 rgba(255, 255, 255, 0.2)',
    border: '1px solid rgba(0, 255, 136, 0.3)',
    '&:before': {
      opacity: 1,
    }
  },
  animation: `${slideUp} 0.6s ease-out`,
}));

const NeonIconBox = styled(Box)(({ theme, color = '#00ff88' }) => ({
  width: 56,
  height: 56,
  borderRadius: '16px',
  background: `linear-gradient(135deg, ${alpha(color, 0.2)} 0%, ${alpha(color, 0.05)} 100%)`,
  border: `1px solid ${alpha(color, 0.3)}`,
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  position: 'relative',
  transition: 'all 0.3s ease',
  '&:before': {
    content: '""',
    position: 'absolute',
    inset: -2,
    borderRadius: '18px',
    background: `linear-gradient(45deg, ${color}, transparent, ${color})`,
    opacity: 0,
    transition: 'opacity 0.3s ease',
    zIndex: -1,
  },
  '&:hover': {
    transform: 'scale(1.1)',
    boxShadow: `0 0 30px ${alpha(color, 0.6)}`,
    '&:before': {
      opacity: 1,
    }
  }
}));

const AnimatedNumber = styled(Typography)(({ theme }) => ({
  background: 'linear-gradient(135deg, #00ff88 0%, #00ccff 100%)',
  backgroundClip: 'text',
  WebkitBackgroundClip: 'text',
  WebkitTextFillColor: 'transparent',
  fontWeight: 800,
  fontSize: '2.5rem',
  lineHeight: 1,
  animation: `${pulse} 2s ease-in-out infinite`,
}));

const GradientText = styled(Typography)(({ gradient = 'linear-gradient(135deg, #00ff88 0%, #00ccff 100%)' }) => ({
  background: gradient,
  backgroundClip: 'text',
  WebkitBackgroundClip: 'text',
  WebkitTextFillColor: 'transparent',
  fontWeight: 700,
}));

const FloatingCard = styled(GlassmorphicCard)(({ theme, delay = 0 }) => ({
  animation: `${float} 3s ease-in-out infinite ${delay}s, ${slideUp} 0.8s ease-out`,
}));

// Premium animated counter with smooth transitions
const PremiumCounter = ({ value, duration = 2000, prefix = '', suffix = '', gradient = 'linear-gradient(135deg, #00ff88 0%, #00ccff 100%)' }) => {
  const [count, setCount] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  
  useEffect(() => {
    setIsVisible(true);
    let start = 0;
    const end = parseFloat(value) || 0;
    if (start === end) {
      setCount(end);
      return;
    }
    
    const startTime = Date.now();
    const timer = setInterval(() => {
      const elapsed = Date.now() - startTime;
      const progress = Math.min(elapsed / duration, 1);
      
      // Easing function for smooth animation
      const easeOutCubic = 1 - Math.pow(1 - progress, 3);
      const current = start + (end - start) * easeOutCubic;
      
      setCount(current);
      
      if (progress >= 1) {
        setCount(end);
        clearInterval(timer);
      }
    }, 16);
    
    return () => clearInterval(timer);
  }, [value, duration]);
  
  return (
    <Zoom in={isVisible} timeout={800}>
      <GradientText 
        variant="h3" 
        gradient={gradient}
        sx={{ 
          fontFamily: '"Inter", "Roboto", sans-serif',
          fontWeight: 900,
          fontSize: { xs: '1.8rem', sm: '2.2rem', md: '2.8rem' },
          textShadow: '0 0 20px rgba(0, 255, 136, 0.3)',
          animation: `${glow} 3s ease-in-out infinite`,
        }}
      >
        {prefix}{Math.round(count).toLocaleString()}{suffix}
      </GradientText>
    </Zoom>
  );
};

// Enhanced KPI Card component
const KPICard = ({ title, value, subtitle, icon: Icon, color, trend, isLoading }) => {
  const theme = useTheme();
  
  if (isLoading) {
    return (
      <Card sx={{ 
        background: 'linear-gradient(145deg, rgba(35, 39, 51, 0.8) 0%, rgba(25, 28, 36, 0.9) 100%)',
        backdropFilter: 'blur(20px)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: '16px',
        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.37)',
        height: '140px'
      }}>
        <CardContent sx={{ p: 3 }}>
          <Skeleton variant="text" width="60%" height={20} />
          <Skeleton variant="text" width="40%" height={40} sx={{ mt: 1 }} />
          <Skeleton variant="text" width="80%" height={16} sx={{ mt: 1 }} />
        </CardContent>
      </Card>
    );
  }
  
  return (
    <Fade in timeout={500}>
      <Card sx={{ 
        background: 'linear-gradient(145deg, rgba(35, 39, 51, 0.8) 0%, rgba(25, 28, 36, 0.9) 100%)',
        backdropFilter: 'blur(20px)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: '16px',
        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.37)',
        transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
        height: '140px',
        position: 'relative',
        overflow: 'hidden',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: '0 20px 40px rgba(0, 0, 0, 0.4)',
          border: `1px solid ${alpha(color, 0.5)}`,
        },
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          height: '3px',
          background: `linear-gradient(90deg, ${color}, ${alpha(color, 0.6)})`,
        }
      }}>
        <CardContent sx={{ p: 3, position: 'relative' }}>
          <Box sx={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'space-between', mb: 2 }}>
            <Typography 
              variant="subtitle2" 
              sx={{ 
                color: '#b0bec5',
                fontWeight: 600,
                fontSize: '0.85rem',
                textTransform: 'uppercase',
                letterSpacing: '0.5px'
              }}
            >
              {title}
            </Typography>
            <Box sx={{ 
              backgroundColor: alpha(color, 0.15),
              borderRadius: '12px',
              p: 1,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              <Icon sx={{ color, fontSize: 20 }} />
            </Box>
          </Box>
          
          <Typography 
            variant="h4" 
            sx={{ 
              color: color,
              fontWeight: 700,
              fontSize: '2rem',
              lineHeight: 1.2,
              mb: 1
            }}
          >
            <AnimatedCounter value={typeof value === 'string' ? value.replace(/[^0-9.]/g, '') : value} />
            {typeof value === 'string' && value.includes('%') && '%'}
          </Typography>
          
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <Typography 
              variant="body2" 
              sx={{ 
                color: '#78909c',
                fontSize: '0.75rem',
                lineHeight: 1.3
              }}
            >
              {subtitle}
            </Typography>
            {trend && (
              <Chip 
                size="small"
                icon={<TrendingUpIcon sx={{ fontSize: 14 }} />}
                label={trend}
                sx={{
                  backgroundColor: alpha('#4caf50', 0.15),
                  color: '#4caf50',
                  fontSize: '0.7rem',
                  height: 20,
                  '& .MuiChip-icon': { color: '#4caf50', fontSize: 12 }
                }}
              />
            )}
          </Box>
        </CardContent>
      </Card>
    </Fade>
  );
};

// Enhanced Chart Card component
const ChartCard = ({ title, children, isLoading, actions }) => {
  if (isLoading) {
    return (
      <Card sx={{ 
        background: 'linear-gradient(145deg, rgba(35, 39, 51, 0.8) 0%, rgba(25, 28, 36, 0.9) 100%)',
        backdropFilter: 'blur(20px)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: '16px',
        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.37)',
        height: '420px'
      }}>
        <CardContent sx={{ p: 3 }}>
          <Skeleton variant="text" width="60%" height={28} />
          <Skeleton variant="rectangular" width="100%" height={350} sx={{ mt: 2, borderRadius: 2 }} />
        </CardContent>
      </Card>
    );
  }
  
  return (
    <Fade in timeout={700}>
      <Card sx={{ 
        background: 'linear-gradient(145deg, rgba(35, 39, 51, 0.8) 0%, rgba(25, 28, 36, 0.9) 100%)',
        backdropFilter: 'blur(20px)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        borderRadius: '16px',
        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.37)',
        transition: 'all 0.3s ease',
        '&:hover': {
          boxShadow: '0 12px 40px rgba(0, 0, 0, 0.5)',
        }
      }}>
        <CardContent sx={{ p: 3 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
            <Typography 
              variant="h6" 
              sx={{ 
                color: '#00e6e6',
                fontWeight: 600,
                fontSize: '1.25rem'
              }}
            >
              {title}
            </Typography>
            {actions && (
              <Box sx={{ display: 'flex', gap: 1 }}>
                {actions}
              </Box>
            )}
          </Box>
          {children}
        </CardContent>
      </Card>
    </Fade>
  );
};

function ValidationKPIs({ refresh }) {
  const [validationData, setValidationData] = useState([]);
  const [reasonsData, setReasonsData] = useState([]);
  const [penaltyMetrics, setPenaltyMetrics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedPO, setSelectedPO] = useState("");
  const [tabValue, setTabValue] = useState(0);
  const theme = useTheme();

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Helper function to parse CSV
  const parseCSV = (csv) => {
    const [header, ...rows] = csv.trim().split(/\r?\n/);
    const keys = header.split(",");
    return rows.map(row => {
      const values = row.split(/,(?=(?:[^"]*"[^"]*")*[^"]*$)/).map(v => v.replace(/^"|"$/g, ""));
      const obj = {};
      keys.forEach((k, i) => obj[k] = values[i]);
      return obj;
    });
  };

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
        // Fetch validation results
        const validationRes = await fetch(`${BACKEND_URL}/results/final_reason_validation_results.csv`);
        const validationText = await validationRes.text();
        
        // Try to fetch from the new endpoint first
        let reasonsParsed = [];
        try {
          const allPenaltiesRes = await fetch(`${BACKEND_URL}/all-penalties`);
          if (allPenaltiesRes.ok) {
            const allPenaltiesData = await allPenaltiesRes.json();
            reasonsParsed = allPenaltiesData.penalties;
          } else {
            // Fallback to direct CSV file
            const reasonsRes = await fetch(`${BACKEND_URL}/results/reasons.csv`);
            const reasonsText = await reasonsRes.text();
            reasonsParsed = parseCSV(reasonsText);
          }
        } catch (error) {
          console.error("Error fetching penalties:", error);
          const reasonsRes = await fetch(`${BACKEND_URL}/results/reasons.csv`);
          const reasonsText = await reasonsRes.text();
          reasonsParsed = parseCSV(reasonsText);
        }
        
        // Parse validation data
        const validationParsed = parseCSV(validationText);
        
        // Make sure penalty values are treated as numbers
        const reasonsWithNumberPenalty = reasonsParsed.map(row => ({
          ...row,
          penalty: row.penalty ? parseFloat(row.penalty) : 0,
          reason_normalized: row.reason ? row.reason.trim().toLowerCase() : ''
        }));
        
        // Enhance validation data with normalized stated_reason
        const enhancedValidationData = validationParsed.map(row => ({
          ...row,
          stated_reason_normalized: row.stated_reason ? row.stated_reason.trim().toLowerCase() : ''
        }));
        
        setValidationData(enhancedValidationData);
        setReasonsData(reasonsWithNumberPenalty);
        
        // Fetch penalty metrics
        const penaltyRes = await fetch(`${BACKEND_URL}/calculate-penalties`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' }
        });
        const penaltyData = await penaltyRes.json();
        
        if (penaltyData.status === 'success') {
          setPenaltyMetrics(penaltyData.metrics);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, [refresh]);

  // Calculate metrics only after data is loaded
  const metrics = React.useMemo(() => {
    if (!validationData.length) {
      return {
        total: 0,
        match: 0,
        notMatch: 0,
        matchDist: [],
        reasonDist: [],
        topReasons: [],
        totalPenaltyAmount: 0,
        saveablePenaltyAmount: 0
      };
    }

    const total = validationData.length;
    let match = 0, notMatch = 0;
    const reasonCounts = {};
    
    validationData.forEach((row) => {
      if (row["Match/Not"] === "True") match++;
      else if (row["Match/Not"] === "False") notMatch++;
      if (row.stated_reason) {
        const reason = row.stated_reason.trim();
        if (reason) reasonCounts[reason] = (reasonCounts[reason] || 0) + 1;
      }
    });
    
    const matchDist = [
      { name: "Match", value: match },
      { name: "Not Match", value: notMatch },
    ];
    
    const reasonDist = Object.entries(reasonCounts).map(([name, value]) => ({ name, value }));
    const filteredReasonDist = reasonDist.filter(r => r.name.trim().toLowerCase() !== "no reason");
    const topReasons = filteredReasonDist.slice().sort((a, b) => b.value - a.value).slice(0, 5);
    
    // Calculate penalty metrics
    const totalPenaltyAmount = penaltyMetrics?.total_penalty_claimed || 
      reasonsData.reduce((sum, row) => {
        const penalty = parseFloat(row.penalty || 0);
        return isNaN(penalty) ? sum : sum + penalty;
      }, 0);
      
    const saveablePenaltyAmount = penaltyMetrics?.penalty_that_can_be_saved ||
      validationData
        .filter(vRow => vRow["Match/Not"] === "False")
        .map(vRow => {
          const reasonRow = reasonsData.find(
            r => r.PO_ID === vRow.PO_ID && 
                 r.reason && vRow.stated_reason &&
                 r.reason.trim().toLowerCase() === vRow.stated_reason.trim().toLowerCase()
          );
          return reasonRow ? parseFloat(reasonRow.penalty || 0) : 0;
        })
        .reduce((sum, penalty) => sum + (isNaN(penalty) ? 0 : penalty), 0);

    return {
      total,
      match,
      notMatch,
      matchDist,
      reasonDist,
      topReasons,
      totalPenaltyAmount,
      saveablePenaltyAmount
    };
  }, [validationData, reasonsData, penaltyMetrics]);

  // PO options for filtering
  const poOptions = React.useMemo(() => {
    return validationData.map(row => row.PO_ID);
  }, [validationData]);

  const filteredRows = selectedPO
    ? validationData.filter(row => row.PO_ID === selectedPO)
    : validationData;

  if (loading) {
    return (
      <Box sx={{ p: 3 }}>
        <Typography variant="h5" align="center" sx={{ mb: 4, fontWeight: 700, color: '#00e6e6' }}>
          Discrepancy Validator
        </Typography>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Grid container spacing={3}>
              {[1, 2, 3, 4].map((i) => (
                <Grid item xs={12} md={3} key={i}>
                  <KPICard isLoading />
                </Grid>
              ))}
            </Grid>
          </Grid>
          <Grid item xs={12} md={6}>
            <ChartCard isLoading />
          </Grid>
          <Grid item xs={12} md={6}>
            <ChartCard isLoading />
          </Grid>
        </Grid>
      </Box>
    );
  }

  return (
    <Box sx={{ 
      p: 3,
      background: 'linear-gradient(135deg, #0a0e1a 0%, #1a1f2e 100%)',
      minHeight: '100vh'
    }}>
      {/* Header */}
      <Box sx={{ textAlign: 'center', mb: 4 }}>
        <Typography 
          variant="h4" 
          sx={{ 
            fontWeight: 700, 
            color: '#00e6e6',
            mb: 1,
            background: 'linear-gradient(45deg, #00e6e6, #1976d2)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}
        >
          Discrepancy Validator Dashboard
        </Typography>
        <Typography variant="subtitle1" sx={{ color: '#78909c', fontWeight: 500 }}>
          Real-time Purchase Order Validation & Analytics
        </Typography>
      </Box>

      <Grid container spacing={3}>
        {/* KPI Cards */}
        <Grid item xs={12}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={3}>
              <KPICard
                title="Total Validations"
                value={metrics.total}
                subtitle="Financial Transactions Analyzed"
                icon={AssessmentIcon}
                color="#00e6e6"
                trend="+12.5%"
                isLoading={loading}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <KPICard
                title="Match Accuracy"
                value={`${metrics.total ? ((metrics.match / metrics.total) * 100).toFixed(1) : 0}%`}
                subtitle="Validated PO to Invoice Alignment"
                icon={CheckIcon}
                color="#4caf50"
                trend="+2.3%"
                isLoading={loading}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <KPICard
                title="Discrepancies Found"
                value={metrics.notMatch}
                subtitle="Identified for Resolution"
                icon={ErrorIcon}
                color="#ff5722"
                isLoading={loading}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <KPICard
                title="Top Issue"
                value={metrics.topReasons.length > 0 ? 
                  (metrics.topReasons[0].name.length > 15 
                    ? metrics.topReasons[0].name.substring(0, 13) + '...' 
                    : metrics.topReasons[0].name) : '-'}
                subtitle={metrics.topReasons.length > 0 ? `${metrics.topReasons[0].value} Occurrences` : 'No data'}
                icon={ShowChartIcon}
                color="#9c27b0"
                isLoading={loading}
              />
            </Grid>
          </Grid>
        </Grid>
        
        {/* Charts Row */}
        <Grid item xs={12} md={6}>
          <ChartCard
            title="Average Penalty by Reason"
            isLoading={loading}
            actions={
              <Tooltip title="Refresh Data">
                <IconButton size="small" sx={{ color: '#78909c' }}>
                  <RefreshIcon />
                </IconButton>
              </Tooltip>
            }
          >
            <Box sx={{ height: 350, display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={metrics.topReasons.slice(0, 5).map((r, index) => {
                      const penaltyAmounts = reasonsData
                        .filter(item => 
                          item.reason && r.name && 
                          item.reason.trim().toLowerCase() === r.name.trim().toLowerCase()
                        )
                        .map(item => parseFloat(item.penalty || 0))
                        .filter(p => !isNaN(p));
                      
                      const avgPenalty = penaltyAmounts.length > 0 
                        ? penaltyAmounts.reduce((sum, p) => sum + p, 0) / penaltyAmounts.length 
                        : 0;
                      
                      return {
                        name: r.name.length > 20 ? r.name.substring(0, 18) + '...' : r.name,
                        value: parseFloat(avgPenalty.toFixed(2)),
                        fullName: r.name,
                        count: penaltyAmounts.length,
                        total: penaltyAmounts.reduce((sum, p) => sum + p, 0)
                      };
                    }).filter(item => item.value > 0)}
                    cx="50%"
                    cy="50%"
                    outerRadius={120}
                    innerRadius={60}
                    paddingAngle={3}
                    dataKey="value"
                  >
                    {metrics.topReasons.slice(0, 5).map((_, index) => (
                      <Cell 
                        key={`cell-${index}`} 
                        fill={COLORS[index % COLORS.length]}
                        stroke="rgba(255,255,255,0.1)"
                        strokeWidth={2}
                      />
                    ))}
                  </Pie>
                  <RechartsTooltip
                    contentStyle={{
                      backgroundColor: 'rgba(35, 39, 51, 0.95)',
                      border: '1px solid rgba(255, 255, 255, 0.1)',
                      borderRadius: '8px',
                      color: '#e3eafc'
                    }}
                    formatter={(value, name, props) => [
                      `$${value.toLocaleString()}`, 
                      'Average Penalty'
                    ]}
                    labelFormatter={(_, payload) => payload?.[0]?.payload?.fullName || ''}
                  />
                  <Legend 
                    verticalAlign="bottom"
                    height={36}
                    iconType="circle"
                    wrapperStyle={{ 
                      color: '#e3eafc', 
                      fontSize: '14px',
                      paddingTop: '20px'
                    }}
                  />
                </PieChart>
              </ResponsiveContainer>
            </Box>
          </ChartCard>
        </Grid>
        
        <Grid item xs={12} md={6}>
          <ChartCard
            title="Top Discrepancy Drivers"
            isLoading={loading}
            actions={
              <Tooltip title="Filter Data">
                <IconButton size="small" sx={{ color: '#78909c' }}>
                  <FilterIcon />
                </IconButton>
              </Tooltip>
            }
          >
            <ResponsiveContainer width="100%" height={350}>
              <BarChart 
                data={metrics.topReasons.map(r => ({
                  name: r.name.length > 20 ? r.name.substring(0, 18) + '...' : r.name,
                  value: r.value,
                  percentage: ((r.value / metrics.total) * 100).toFixed(1),
                  fullName: r.name
                }))} 
                layout="vertical" 
                margin={{ left: 30, right: 30, top: 20, bottom: 20 }}
              >
                <XAxis 
                  type="number" 
                  allowDecimals={false}
                  axisLine={false}
                  tickLine={false}
                  tick={{ fill: '#78909c', fontSize: 12 }}
                />
                <YAxis 
                  dataKey="name" 
                  type="category" 
                  axisLine={false}
                  tickLine={false}
                  tick={{ fill: '#e3eafc', fontSize: 13, fontWeight: 500 }} 
                  width={150}
                />
                <Bar 
                  dataKey="value" 
                  fill="url(#barGradient)"
                  barSize={30}
                  radius={[0, 6, 6, 0]}
                />
                <RechartsTooltip
                  contentStyle={{
                    backgroundColor: 'rgba(35, 39, 51, 0.95)',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    borderRadius: '8px',
                    color: '#e3eafc'
                  }}
                  formatter={(value, name, props) => [
                    `${value} occurrences (${props.payload.percentage}%)`, 
                    'Frequency'
                  ]}
                  labelFormatter={(label, payload) => payload?.[0]?.payload?.fullName || label}
                />
                <defs>
                  <linearGradient id="barGradient" x1="0" y1="0" x2="1" y2="0">
                    <stop offset="0%" stopColor="#1976d2" />
                    <stop offset="100%" stopColor="#00e6e6" />
                  </linearGradient>
                </defs>
              </BarChart>
            </ResponsiveContainer>
          </ChartCard>
        </Grid>
        
        {/* Enhanced Table */}
        <Grid item xs={12}>
          <ChartCard
            title="Validation Insights Overview"
            isLoading={loading}
          >
            <TableContainer 
              component={Paper} 
              sx={{ 
                backgroundColor: 'transparent',
                boxShadow: 'none',
                border: '1px solid rgba(255, 255, 255, 0.1)',
                borderRadius: '12px',
                maxHeight: 300,
                '&::-webkit-scrollbar': {
                  width: '8px',
                },
                '&::-webkit-scrollbar-track': {
                  backgroundColor: 'rgba(255, 255, 255, 0.1)',
                  borderRadius: '4px',
                },
                '&::-webkit-scrollbar-thumb': {
                  backgroundColor: '#00e6e6',
                  borderRadius: '4px',
                },
              }}
            >
              <Table size="small" stickyHeader>
                <TableHead>
                  <TableRow>
                    {['Reason', 'Count', 'Accuracy', 'Avg Penalty', 'Total Impact'].map((header) => (
                      <TableCell 
                        key={header}
                        sx={{ 
                          backgroundColor: 'rgba(35, 39, 51, 0.9)',
                          color: '#00e6e6',
                          fontWeight: 700,
                          fontSize: '0.9rem',
                          borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
                          textTransform: 'uppercase',
                          letterSpacing: '0.5px'
                        }}
                      >
                        {header}
                      </TableCell>
                    ))}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {metrics.topReasons.slice(0, 8).map((r, index) => {
                    const matchCount = validationData.filter(row => 
                      row.stated_reason && 
                      row.stated_reason.trim().toLowerCase() === r.name.trim().toLowerCase() && 
                      row["Match/Not"] === "True"
                    ).length;
                    
                    const notMatchCount = validationData.filter(row => 
                      row.stated_reason && 
                      row.stated_reason.trim().toLowerCase() === r.name.trim().toLowerCase() && 
                      row["Match/Not"] === "False"
                    ).length;
                    
                    const totalCount = matchCount + notMatchCount;
                    const matchRate = totalCount ? (matchCount / totalCount) * 100 : 0;
                    
                    const penaltyAmounts = reasonsData
                      .filter(item => 
                        item.reason && r.name && 
                        item.reason.trim().toLowerCase() === r.name.trim().toLowerCase()
                      )
                      .map(item => parseFloat(item.penalty || 0))
                      .filter(p => !isNaN(p));
                    
                    const totalPenalty = penaltyAmounts.reduce((sum, p) => sum + p, 0);
                    const avgPenalty = penaltyAmounts.length > 0 ? totalPenalty / penaltyAmounts.length : 0;
                    
                    return (
                      <TableRow 
                        key={r.name} 
                        sx={{ 
                          backgroundColor: index % 2 === 0 ? 'rgba(255, 255, 255, 0.02)' : 'transparent',
                          '&:hover': { 
                            backgroundColor: 'rgba(0, 230, 230, 0.05)',
                            transform: 'scale(1.01)',
                            transition: 'all 0.2s ease'
                          }
                        }}
                      >
                        <TableCell sx={{ 
                          color: '#e3eafc', 
                          fontWeight: 500,
                          maxWidth: '200px',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          whiteSpace: 'nowrap'
                        }}>
                          <Tooltip title={r.name}>
                            <span>{r.name}</span>
                          </Tooltip>
                        </TableCell>
                        <TableCell sx={{ color: '#b0bec5', textAlign: 'center' }}>
                          <Chip 
                            label={totalCount}
                            size="small"
                            sx={{ 
                              backgroundColor: alpha('#1976d2', 0.2),
                              color: '#1976d2',
                              fontWeight: 600
                            }}
                          />
                        </TableCell>
                        <TableCell sx={{ textAlign: 'center' }}>
                          <Chip 
                            label={`${matchRate.toFixed(1)}%`}
                            size="small"
                            sx={{ 
                              backgroundColor: alpha(
                                matchRate < 70 ? '#f44336' : matchRate < 90 ? '#ff9800' : '#4caf50', 
                                0.2
                              ),
                              color: matchRate < 70 ? '#f44336' : matchRate < 90 ? '#ff9800' : '#4caf50',
                              fontWeight: 600
                            }}
                          />
                        </TableCell>
                        <TableCell sx={{ color: '#b0bec5', textAlign: 'right', fontWeight: 500 }}>
                          ${avgPenalty.toFixed(2)}
                        </TableCell>
                        <TableCell sx={{ 
                          color: '#00e6e6', 
                          textAlign: 'right', 
                          fontWeight: 700,
                          fontSize: '0.9rem'
                        }}>
                          ${totalPenalty.toLocaleString()}
                        </TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </TableContainer>
          </ChartCard>
        </Grid>
        
        {/* Enhanced Tabs Section */}
        <Grid item xs={12}>
          <Card sx={{ 
            background: 'linear-gradient(145deg, rgba(35, 39, 51, 0.8) 0%, rgba(25, 28, 36, 0.9) 100%)',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            borderRadius: '16px',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.37)'
          }}>
            <Box sx={{ borderBottom: 1, borderColor: 'rgba(255, 255, 255, 0.1)' }}>
              <Tabs 
                value={tabValue} 
                onChange={handleTabChange}
                sx={{
                  '& .MuiTab-root': {
                    color: '#78909c',
                    fontWeight: 600,
                    textTransform: 'none',
                    fontSize: '1rem',
                    '&.Mui-selected': {
                      color: '#00e6e6'
                    }
                  },
                  '& .MuiTabs-indicator': {
                    backgroundColor: '#00e6e6',
                    height: '3px',
                    borderRadius: '2px'
                  }
                }}
              >
                <Tab label="Detailed Analysis" />
                <Tab label="Penalty Breakdown" />
              </Tabs>
            </Box>
            
            <CardContent sx={{ p: 3 }}>
              {tabValue === 0 && (
                <Fade in timeout={300}>
                  <Box>
                    <FormControl fullWidth sx={{ mb: 3 }}>
                      <InputLabel 
                        sx={{ color: '#78909c' }}
                        id="po-select-label"
                      >
                        Filter by Purchase Order
                      </InputLabel>
                      <Select
                        labelId="po-select-label"
                        value={selectedPO}
                        label="Filter by Purchase Order"
                        onChange={e => setSelectedPO(e.target.value)}
                        sx={{
                          '& .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(255, 255, 255, 0.2)'
                          },
                          '&:hover .MuiOutlinedInput-notchedOutline': {
                            borderColor: '#00e6e6'
                          },
                          '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                            borderColor: '#00e6e6'
                          },
                          '& .MuiSelect-select': {
                            color: '#e3eafc'
                          }
                        }}
                      >
                        <MenuItem value="">All Purchase Orders</MenuItem>
                        {poOptions.map((po, index) => (
                          <MenuItem key={index} value={po}>{po}</MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                    
                    <TableContainer 
                      component={Paper}
                      sx={{ 
                        backgroundColor: 'transparent',
                        boxShadow: 'none',
                        border: '1px solid rgba(255, 255, 255, 0.1)',
                        borderRadius: '12px',
                        maxHeight: 400
                      }}
                    >
                      <Table size="small" stickyHeader>
                        <TableHead>
                          <TableRow>
                            {['PO ID', 'Reason', 'Status', 'Comments'].map((header) => (
                              <TableCell 
                                key={header}
                                sx={{ 
                                  backgroundColor: 'rgba(35, 39, 51, 0.9)',
                                  color: '#00e6e6',
                                  fontWeight: 700,
                                  borderBottom: '1px solid rgba(255, 255, 255, 0.1)'
                                }}
                              >
                                {header}
                              </TableCell>
                            ))}
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {filteredRows.slice(0, 50).map((row, idx) => (
                            <TableRow 
                              key={idx}
                              sx={{ 
                                '&:hover': { 
                                  backgroundColor: 'rgba(0, 230, 230, 0.05)' 
                                }
                              }}
                            >
                              <TableCell sx={{ color: '#e3eafc', fontWeight: 500 }}>
                                {row.PO_ID}
                              </TableCell>
                              <TableCell sx={{ color: '#b0bec5' }}>
                                {row.stated_reason}
                              </TableCell>
                              <TableCell>
                                <Chip 
                                  label={row["Match/Not"] === "True" ? "Match" : "No Match"}
                                  size="small"
                                  sx={{ 
                                    backgroundColor: alpha(
                                      row["Match/Not"] === "True" ? '#4caf50' : '#f44336', 
                                      0.2
                                    ),
                                    color: row["Match/Not"] === "True" ? '#4caf50' : '#f44336',
                                    fontWeight: 600
                                  }}
                                />
                              </TableCell>
                              <TableCell sx={{ 
                                color: '#78909c',
                                maxWidth: '300px',
                                overflow: 'hidden',
                                textOverflow: 'ellipsis',
                                whiteSpace: 'nowrap'
                              }}>
                                {row.Comments || '-'}
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </Box>
                </Fade>
              )}
              
              {tabValue === 1 && (
                <Fade in timeout={300}>
                  <Box>
                    <PenaltyTable validationData={validationData} reasonsData={reasonsData} />
                  </Box>
                </Fade>
              )}
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
}

export default ValidationKPIs;
