import React, { useEffect, useState } from "react";
import { Box, Grid, Typography, CircularProgress, FormControl, InputLabel, Select, MenuItem, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from "@mui/material";
import { PieChart, Pie, Cell, Tooltip, Legend, BarChart, Bar, XAxis, YAxis, ResponsiveContainer, LineChart, Line, Text } from "recharts";
import { BACKEND_URL } from "../utils/backend";

const COLORS = ["#1976d2", "#43a047", "#fbc02d", "#e53935", "#8e24aa", "#00acc1"];

function TaggingKPIs({ refresh }) {
  const [reasonsData, setReasonsData] = useState([]);
  const [commentsData, setCommentsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedPO, setSelectedPO] = useState("");

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      const listRes = await fetch(`${BACKEND_URL}/results/filtered_po_reason_list.csv`);
      const listText = await listRes.text();
      const commentsRes = await fetch(`${BACKEND_URL}/results/filtered_po_reason_comments.csv`);
      const commentsText = await commentsRes.text();
      // Parse CSVs
      const parseCSV = (csv) => {
        const [header, ...rows] = csv.trim().split(/\r?\n/);
        const keys = header.split(",");
        return rows.map(row => {
          const values = row.split(/,(?=(?:[^"]*"[^"]*")*[^"]*$)/).map(v => v.replace(/^"|"$/g, ""));
          const obj = {};
          keys.forEach((k, i) => obj[k] = values[i]);
          return obj;
        });
      };
  const parsedReasons = parseCSV(listText);
  const parsedComments = parseCSV(commentsText);
  console.log('parsedReasons', parsedReasons);
  console.log('parsedComments', parsedComments);
  setReasonsData(parsedReasons);
  setCommentsData(parsedComments);
      setLoading(false);
    }
    fetchData();
  }, [refresh]);

  if (loading) return <CircularProgress />;


  // KPI: Total POs, Unique Reasons, Reason Distribution
  const totalPOs = reasonsData.length;
  const reasonCounts = {};
  reasonsData.forEach((row) => {
    if (row.list_of_reasons) {
      row.list_of_reasons.split(",").forEach((r) => {
        const reason = r.trim();
        if (reason) reasonCounts[reason] = (reasonCounts[reason] || 0) + 1;
      });
    }
  });
  const reasonDist = Object.entries(reasonCounts).map(([name, value], i) => ({ name, value }));

  // Trend: POs with multiple reasons
  const multiReasonPOs = reasonsData.filter(row => row.list_of_reasons && row.list_of_reasons.split(",").length > 1).length;

  // Top 5 reasons for pie chart, top 3 for bar chart
  const top5Reasons = reasonDist.slice().sort((a, b) => b.value - a.value).slice(0, 5);
  const top3Reasons = reasonDist.slice().sort((a, b) => b.value - a.value).slice(0, 3);

  // PO slicer
  const poOptions = reasonsData.map(row => row.PO_ID);
  const filteredComments = selectedPO
    ? commentsData.filter(row => row.PO_ID === selectedPO)
    : commentsData;

  return (
    <Box>
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <Typography variant="subtitle1">Total POs</Typography>
          <Typography variant="h4" color="primary">{totalPOs}</Typography>
        </Grid>
        <Grid item xs={6}>
          <Typography variant="subtitle1">POs with Multiple Reasons</Typography>
          <Typography variant="h4" color="secondary">{multiReasonPOs}</Typography>
        </Grid>
        <Grid item xs={12} md={12} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <Typography variant="subtitle2" mb={1} align="center">Reason Distribution (Pie)</Typography>
          <Box sx={{ width: 340, height: 220 }}>
            <ResponsiveContainer width="100%" height="100%">
              <PieChart>
                <Pie
                  data={top5Reasons}
                  dataKey="value"
                  nameKey="name"
                  cx="50%"
                  cy="50%"
                  outerRadius={70}
                  label={({ name, value, cx, cy, midAngle, outerRadius }) => {
                    // Custom label further outside pie with connector line
                    const RADIAN = Math.PI / 180;
                    const labelRadius = outerRadius + 60;
                    const x = cx + labelRadius * Math.cos(-midAngle * RADIAN);
                    const y = cy + labelRadius * Math.sin(-midAngle * RADIAN);
                    const sx = cx + (outerRadius + 10) * Math.cos(-midAngle * RADIAN);
                    const sy = cy + (outerRadius + 10) * Math.sin(-midAngle * RADIAN);
                    return (
                      <g>
                        <line x1={sx} y1={sy} x2={x} y2={y} stroke="#888" strokeWidth={1} />
                        <text x={x} y={y} fontWeight="bold" fill="#111" fontSize={13} textAnchor={x > cx ? "start" : "end"} dominantBaseline="central">
                          {name}: {value}
                        </text>
                      </g>
                    );
                  }}
                >
                  {top5Reasons.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </Box>
        </Grid>
        <Grid item xs={12} md={6}>
          <Typography variant="subtitle2" mb={1}>Top 3 Reasons (Bar)</Typography>
          <ResponsiveContainer width="100%" height={120}>
            <BarChart data={top3Reasons}>
                <XAxis
                  dataKey="name"
                  interval={0}
                  tick={({ x, y, payload }) => (
                    <Text x={x} y={y + 20} textAnchor="middle" verticalAnchor="start" fontWeight="bold" fill="#111" fontSize={14}>
                      {payload.value}
                    </Text>
                  )}
                  height={60}
                />
              <YAxis allowDecimals={false} tick={{ fontWeight: 'bold', fill: '#111' }} />
              <Bar dataKey="value" fill="#1976d2" barSize={40} label={{ position: 'top', fontWeight: 'bold', fill: '#111' }} />
              <Tooltip />
            </BarChart>
          </ResponsiveContainer>
        </Grid>
          <Grid item xs={12} md={12}>
            <Typography variant="subtitle2" mb={1}>Top 5 Reasons Trend (Line)</Typography>
            <ResponsiveContainer width="100%" height={220}>
              <LineChart data={top5Reasons} margin={{ left: 40, right: 40 }}>
                <XAxis dataKey="name" tick={{ fontWeight: 'bold', fill: '#111', fontSize: 15 }} />
                <YAxis allowDecimals={false} tick={{ fontWeight: 'bold', fill: '#111' }} />
                <Line type="monotone" dataKey="value" stroke="#43a047" strokeWidth={3} dot={{ r: 5 }} />
                <Tooltip />
              </LineChart>
            </ResponsiveContainer>
          </Grid>
        <Grid item xs={12} md={12}>
          <Box mt={3}>
            <FormControl fullWidth>
              <InputLabel id="po-select-label">Select PO</InputLabel>
              <Select
                labelId="po-select-label"
                value={selectedPO}
                label="Select PO"
                onChange={e => setSelectedPO(e.target.value)}
              >
                <MenuItem value="">All</MenuItem>
                {poOptions.map(po => (
                  <MenuItem key={po} value={po}>{po}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <Box mt={2}>
              <Typography variant="subtitle2">PO-wise Reasons & Comments</Typography>
              <TableContainer component={Paper} sx={{ maxHeight: 300 }}>
                <Table size="small" stickyHeader>
                  <TableHead>
                    <TableRow>
                      <TableCell>PO ID</TableCell>
                      <TableCell>Reason</TableCell>
                      <TableCell>Comments</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {filteredComments.map((row, idx) => (
                      <TableRow key={idx}>
                        <TableCell>{row.PO_ID}</TableCell>
                        <TableCell>{row.reason}</TableCell>
                        <TableCell>{row.comments}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
}

export default TaggingKPIs;
