import React, { useEffect, useState } from "react";
import { Box, Grid, Typography, CircularProgress, FormControl, InputLabel, Select, MenuItem, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Card, CardContent } from "@mui/material";
import { PieChart, Pie, Cell, Tooltip, Legend, BarChart, Bar, XAxis, YAxis, ResponsiveContainer, LineChart, Line } from "recharts";
import { BACKEND_URL } from "../utils/backend";

const COLORS = ["#1976d2", "#43a047", "#fbc02d", "#e53935", "#8e24aa", "#00acc1"];

function ValidationKPIs({ refresh }) {
  const [validationData, setValidationData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedPO, setSelectedPO] = useState("");

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      const res = await fetch(`${BACKEND_URL}/results/final_reason_validation_results.csv`);
      const text = await res.text();
      // Parse CSV
      const parseCSV = (csv) => {
        const [header, ...rows] = csv.trim().split(/\r?\n/);
        const keys = header.split(",");
        return rows.map(row => {
          const values = row.split(/,(?=(?:[^"]*"[^"]*")*[^"]*$)/).map(v => v.replace(/^"|"$/g, ""));
          const obj = {};
          keys.forEach((k, i) => obj[k] = values[i]);
          return obj;
        });
      };
      setValidationData(parseCSV(text));
      setLoading(false);
    }
    fetchData();
  }, [refresh]);

  if (loading) return <CircularProgress />;

  // KPI: Total Validations, Match/Not, Distribution
  const total = validationData.length;
  let match = 0, notMatch = 0;
  const reasonCounts = {};
  validationData.forEach((row) => {
    if (row["Match/Not"] === "True") match++;
    else if (row["Match/Not"] === "False") notMatch++;
    if (row.stated_reason) {
      const reason = row.stated_reason.trim();
      if (reason) reasonCounts[reason] = (reasonCounts[reason] || 0) + 1;
    }
  });
  const matchDist = [
    { name: "Match", value: match },
    { name: "Not Match", value: notMatch },
  ];
  const reasonDist = Object.entries(reasonCounts).map(([name, value]) => ({ name, value }));

  // Show only top 3 reasons for better visibility
  const topReasons = reasonDist.slice().sort((a, b) => b.value - a.value).slice(0, 3);

  // PO slicer
  const poOptions = validationData.map(row => row.PO_ID);
  const filteredRows = selectedPO
    ? validationData.filter(row => row.PO_ID === selectedPO)
    : validationData;

  return (
    <Box>
      <Grid container spacing={2}>
        {/* KPI Row at top */}
        <Grid item xs={12}>
          <Card sx={{ mb: 2 }}>
            <CardContent>
              <Grid container spacing={2}>
                <Grid item xs={6} md={3}>
                  <Typography variant="subtitle2">Total Validations</Typography>
                  <Typography variant="h5" color="primary">{total}</Typography>
                </Grid>
                <Grid item xs={6} md={3}>
                  <Typography variant="subtitle2">Match %</Typography>
                  <Typography variant="h5" color="secondary">{total ? ((match / total) * 100).toFixed(1) : 0}%</Typography>
                </Grid>
                <Grid item xs={6} md={3}>
                  <Typography variant="subtitle2">POs with Discrepancy</Typography>
                  <Typography variant="h5">{notMatch}</Typography>
                </Grid>
                <Grid item xs={6} md={3}>
                  <Typography variant="subtitle2">Most Frequent Reason</Typography>
                  <Typography variant="h6">{topReasons.length > 0 ? topReasons[0].name : '-'}</Typography>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
        {/* Column Chart: Match vs Not Match (centered) */}
        <Grid item xs={12} md={6} sx={{ display: 'flex', justifyContent: 'center' }}>
          <Card sx={{ mb: 2, width: 340 }}>
            <CardContent>
              <Typography variant="h6" align="center" mb={2}>Match vs Not Match</Typography>
              <ResponsiveContainer width="100%" height={260}>
                <BarChart data={matchDist} margin={{ left: 20, right: 20 }}>
                  <XAxis dataKey="name" tick={{ fontSize: 15 }} />
                  <YAxis allowDecimals={false} />
                  <Bar dataKey="value" fill="#1976d2" barSize={60} />
                  <Tooltip />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>
        {/* Horizontal Bar Chart: Reason Frequency */}
        <Grid item xs={12} md={6}>
          <Card sx={{ mb: 2 }}>
            <CardContent>
              <Typography variant="h6" align="center" mb={2}>Reason Frequency</Typography>
              <ResponsiveContainer width="100%" height={260}>
                <BarChart data={reasonDist.slice().sort((a, b) => b.value - a.value)} layout="vertical" margin={{ left: 40, right: 40 }}>
                  <XAxis type="number" allowDecimals={false} />
                  <YAxis dataKey="name" type="category" tick={{ fontWeight: 'bold', fill: '#111', fontSize: 13 }} width={150} />
                  <Bar dataKey="value" fill="#1976d2" barSize={18} />
                  <Tooltip />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </Grid>
        {/* Match vs Not Match Pie Chart */}
        <Grid item xs={12} md={6}>
          <Card sx={{ mb: 2, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <CardContent sx={{ width: '100%' }}>
              <Typography variant="h6" align="center" mb={2}>Match vs Not Match</Typography>
              <Box sx={{ width: 320, height: 200, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <ResponsiveContainer width="100%" height={160}>
                  <PieChart>
                    <Pie data={matchDist} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={60} label={({ name }) => name} labelLine={false}>
                      {matchDist.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip />
                  </PieChart>
                </ResponsiveContainer>
                <Box sx={{ mt: 1, display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
                  {matchDist.map((entry, index) => (
                    <Box key={entry.name} sx={{ display: 'flex', alignItems: 'center', mr: 2, mb: 1 }}>
                      <Box sx={{ width: 16, height: 16, bgcolor: COLORS[index % COLORS.length], borderRadius: '50%', mr: 1 }} />
                      <Typography variant="body2">{entry.name} ({entry.value})</Typography>
                    </Box>
                  ))}
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={12}>
          <Box mt={3}>
            <FormControl fullWidth>
              <InputLabel id="po-select-label">Select PO</InputLabel>
              <Select
                labelId="po-select-label"
                value={selectedPO}
                label="Select PO"
                onChange={e => setSelectedPO(e.target.value)}
              >
                <MenuItem value="">All</MenuItem>
                {poOptions.map(po => (
                  <MenuItem key={po} value={po}>{po}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <Box mt={2}>
              <Typography variant="subtitle2">PO-wise Reasons & Comments</Typography>
              <TableContainer component={Paper} sx={{ maxHeight: 300 }}>
                <Table size="small" stickyHeader>
                  <TableHead>
                    <TableRow>
                      <TableCell>PO ID</TableCell>
                      <TableCell>Reason</TableCell>
                      <TableCell>Match/Not</TableCell>
                      <TableCell>Comments</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {filteredRows.map((row, idx) => (
                      <TableRow key={idx}>
                        <TableCell>{row.PO_ID}</TableCell>
                        <TableCell>{row.stated_reason}</TableCell>
                        <TableCell>{row["Match/Not"]}</TableCell>
                        <TableCell>{row.Comments}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
}

export default ValidationKPIs;
