import React, { useEffect, useState } from "react";
import { Box, Grid, Typography } from "@mui/material";
import { PieChart, Pie, Cell, Tooltip, Legend, BarChart, Bar, XAxis, YAxis, ResponsiveContainer } from "recharts";
import Papa from "papaparse";

const COLORS = ["#1976d2", "#43a047", "#fbc02d", "#e53935", "#8e24aa", "#00acc1"];

function TaggingKPIs() {
  const [reasonsData, setReasonsData] = useState([]);
  const [commentsData, setCommentsData] = useState([]);

  useEffect(() => {
    // Load CSVs from public folder (simulate API)
    Papa.parse(process.env.PUBLIC_URL + "/filtered_po_reason_list.csv", {
      download: true,
      header: true,
      complete: (result) => setReasonsData(result.data),
    });
    Papa.parse(process.env.PUBLIC_URL + "/filtered_po_reason_comments.csv", {
      download: true,
      header: true,
      complete: (result) => setCommentsData(result.data),
    });
  }, []);

  // KPI: Total POs, Unique Reasons, Reason Distribution
  const totalPOs = reasonsData.length;
  const reasonCounts = {};
  reasonsData.forEach((row) => {
    if (row.list_of_reasons) {
      row.list_of_reasons.split(",").forEach((r) => {
        const reason = r.trim();
        if (reason) reasonCounts[reason] = (reasonCounts[reason] || 0) + 1;
      });
    }
  });
  const reasonDist = Object.entries(reasonCounts).map(([name, value], i) => ({ name, value }));

  return (
    <Box>
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <Typography variant="subtitle1">Total POs</Typography>
          <Typography variant="h4" color="primary">{totalPOs}</Typography>
        </Grid>
        <Grid item xs={6}>
          <Typography variant="subtitle1">Unique Reasons</Typography>
          <Typography variant="h4" color="secondary">{Object.keys(reasonCounts).length}</Typography>
        </Grid>
        <Grid item xs={12} md={6}>
          <Typography variant="subtitle2" mb={1}>Reason Distribution</Typography>
          <ResponsiveContainer width="100%" height={200}>
            <PieChart>
              <Pie data={reasonDist} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={60} label>
                {reasonDist.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </Grid>
        <Grid item xs={12} md={6}>
          <Typography variant="subtitle2" mb={1}>POs per Reason (Bar)</Typography>
          <ResponsiveContainer width="100%" height={200}>
            <BarChart data={reasonDist}>
              <XAxis dataKey="name" />
              <YAxis allowDecimals={false} />
              <Bar dataKey="value" fill="#1976d2" />
              <Tooltip />
            </BarChart>
          </ResponsiveContainer>
        </Grid>
      </Grid>
    </Box>
  );
}

export default TaggingKPIs;
