import React, { useState } from "react";
import { Box, Typography, Button } from "@mui/material";
import TaggingKPIs from "./TaggingKPIs";
import { BACKEND_URL } from "../utils/backend";

function Tagging() {
  const [running, setRunning] = useState(false);
  const [runMsg, setRunMsg] = useState("");
  const [refresh, setRefresh] = useState(0);

  const handleRunWorkflow = async () => {
    setRunning(true);
    setRunMsg("");
    try {
      const res = await fetch(`${BACKEND_URL}/run-tagging`, { method: "POST" });
      const data = await res.json();
      setRunMsg(data.status === "success" ? "Tagging workflow completed!" : "Workflow failed.");
      setRefresh(r => r + 1); // trigger refresh
    } catch (e) {
      setRunMsg("Error running workflow.");
    }
    setRunning(false);
  };

  return (
    <Box sx={{ bgcolor: '#fafafa', minHeight: '100vh', p: 3 }}>
      <Box sx={{ 
        display: 'flex', 
        alignItems: 'center', 
        mb: 4, 
        justifyContent: 'space-between',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        borderRadius: '16px',
        p: 3,
        color: 'white',
        boxShadow: '0 8px 32px rgba(0,0,0,0.12)'
      }}>
        <Box>
          <Typography variant="h4" fontWeight={700} sx={{ mb: 1, letterSpacing: '-0.5px' }}>
            ðŸš© Potential Risk Flagger
          </Typography>
          <Typography variant="subtitle1" sx={{ opacity: 0.9, fontWeight: 400 }}>
            AI-Powered Transaction Risk Detection & Analysis
          </Typography>
        </Box>
        <Button 
          variant="contained" 
          onClick={handleRunWorkflow} 
          disabled={running}
          sx={{
            bgcolor: 'rgba(255,255,255,0.2)',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(255,255,255,0.3)',
            color: 'white',
            fontWeight: 600,
            px: 3,
            py: 1.5,
            borderRadius: '12px',
            textTransform: 'none',
            fontSize: '16px',
            '&:hover': {
              bgcolor: 'rgba(255,255,255,0.3)',
              transform: 'translateY(-2px)',
              boxShadow: '0 8px 25px rgba(0,0,0,0.15)'
            },
            '&:disabled': {
              bgcolor: 'rgba(255,255,255,0.1)',
              color: 'rgba(255,255,255,0.7)'
            }
          }}
        >
          {running ? (
            <>
              <CircularProgress size={20} sx={{ mr: 1, color: 'white' }} />
              Analyzing...
            </>
          ) : (
            'â–¶ Run Analysis'
          )}
        </Button>
      </Box>
      {runMsg && (
        <Typography 
          variant="body1" 
          sx={{ 
            mb: 3,
            p: 2,
            bgcolor: '#e8f5e8',
            color: '#2e7d32',
            borderRadius: '8px',
            border: '1px solid #c8e6c9',
            fontWeight: 500
          }}
        >
          âœ“ {runMsg}
        </Typography>
      )}
      <TaggingKPIs refresh={refresh} />
    </Box>
  );
}

export default Tagging;
