import React, { useEffect, useState } from "react";
import { Box, Grid, Typography, CircularProgress, FormControl, InputLabel, Select, MenuItem, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Card, CardContent } from "@mui/material";
import { PieChart, Pie, Cell, Tooltip, Legend, BarChart, Bar, XAxis, YAxis, ResponsiveContainer, LineChart, Line } from "recharts";
import { BACKEND_URL } from "../utils/backend";

const COLORS = ["#1976d2", "#43a047", "#fbc02d", "#e53935", "#8e24aa", "#00acc1"];

function ValidationKPIs({ refresh }) {
  const [validationData, setValidationData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedPO, setSelectedPO] = useState("");

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      const res = await fetch(`${BACKEND_URL}/results/final_reason_validation_results.csv`);
      const text = await res.text();
      // Parse CSV
      const parseCSV = (csv) => {
        const [header, ...rows] = csv.trim().split(/\r?\n/);
        const keys = header.split(",");
        return rows.map(row => {
          const values = row.split(/,(?=(?:[^"]*"[^"]*")*[^"]*$)/).map(v => v.replace(/^"|"$/g, ""));
          const obj = {};
          keys.forEach((k, i) => obj[k] = values[i]);
          return obj;
        });
      };
      setValidationData(parseCSV(text));
      setLoading(false);
    }
    fetchData();
  }, [refresh]);

  if (loading) return <CircularProgress />;

  // KPI: Total Validations, Match/Not, Distribution
  const total = validationData.length;
  let match = 0, notMatch = 0;
  const reasonCounts = {};
  validationData.forEach((row) => {
    if (row["Match/Not"] === "True") match++;
    else if (row["Match/Not"] === "False") notMatch++;
    if (row.stated_reason) {
      const reason = row.stated_reason.trim();
      if (reason) reasonCounts[reason] = (reasonCounts[reason] || 0) + 1;
    }
  });
  const matchDist = [
    { name: "Match", value: match },
    { name: "Not Match", value: notMatch },
  ];
  const reasonDist = Object.entries(reasonCounts).map(([name, value]) => ({ name, value }));

  // Show only top 3 reasons for better visibility
  const topReasons = reasonDist.slice().sort((a, b) => b.value - a.value).slice(0, 3);

  // PO slicer
  const poOptions = validationData.map(row => row.PO_ID);
  const filteredRows = selectedPO
    ? validationData.filter(row => row.PO_ID === selectedPO)
    : validationData;

  return (
    <Box>
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <Typography variant="subtitle1">Total Validations</Typography>
          <Typography variant="h4" color="primary">{total}</Typography>
        </Grid>
        <Grid item xs={6}>
          <Typography variant="subtitle1">Match %</Typography>
          <Typography variant="h4" color="secondary">{total ? ((match / total) * 100).toFixed(1) : 0}%</Typography>
        </Grid>
        <Grid item xs={12} md={6}>
          <Typography variant="subtitle2" mb={1}>Match vs Not Match</Typography>
          <ResponsiveContainer width="100%" height={200}>
            <PieChart>
              <Pie data={matchDist} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={60} label>
                {matchDist.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </Grid>
        <Grid item xs={12} md={6}>
          <Typography variant="subtitle2" mb={1}>Top 3 Validation Reasons (Bar)</Typography>
          <ResponsiveContainer width="100%" height={250}>
            <BarChart data={topReasons} margin={{ left: 40, right: 40 }}>
              <XAxis dataKey="name" interval={0} tick={{ fontSize: 14, angle: -20, dx: 0, dy: 10, wordBreak: 'break-all' }} />
              <YAxis allowDecimals={false} />
              <Bar dataKey="value" fill="#43a047" barSize={40} />
              <Tooltip />
            </BarChart>
          </ResponsiveContainer>
        </Grid>
        <Grid item xs={12} md={6}>
          <Typography variant="subtitle2" mb={1}>Reason Distribution (Pie)</Typography>
          <ResponsiveContainer width="100%" height={250}>
            <PieChart>
              <Pie data={topReasons} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={80} label>
                {topReasons.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              {/* Legend removed for clarity */}
            </PieChart>
          </ResponsiveContainer>
        </Grid>
        <Grid item xs={12} md={12}>
          <Box mt={2}>
            <Card sx={{ background: '#f5f5f5', mb: 2 }}>
              <CardContent>
                <Typography variant="h6" color="primary">PO Validation Summary</Typography>
                <TableContainer>
                  <Table size="small">
                    <TableHead>
                      <TableRow>
                        <TableCell>Total POs</TableCell>
                        <TableCell>POs with Match</TableCell>
                        <TableCell>POs Correct (QA)</TableCell>
                        <TableCell>POs with Discrepancy</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      <TableRow>
                        <TableCell>{total}</TableCell>
                        <TableCell>{match}</TableCell>
                        <TableCell>{match}</TableCell>
                        <TableCell>{notMatch}</TableCell>
                      </TableRow>
                    </TableBody>
                  </Table>
                </TableContainer>
              </CardContent>
            </Card>
          </Box>
        </Grid>
        <Grid item xs={12} md={12}>
          <Box mt={3}>
            <FormControl fullWidth>
              <InputLabel id="po-select-label">Select PO</InputLabel>
              <Select
                labelId="po-select-label"
                value={selectedPO}
                label="Select PO"
                onChange={e => setSelectedPO(e.target.value)}
              >
                <MenuItem value="">All</MenuItem>
                {poOptions.map(po => (
                  <MenuItem key={po} value={po}>{po}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <Box mt={2}>
              <Typography variant="subtitle2">PO-wise Reasons & Comments</Typography>
              <TableContainer component={Paper} sx={{ maxHeight: 300 }}>
                <Table size="small" stickyHeader>
                  <TableHead>
                    <TableRow>
                      <TableCell>PO ID</TableCell>
                      <TableCell>Reason</TableCell>
                      <TableCell>Match/Not</TableCell>
                      <TableCell>Comments</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {filteredRows.map((row, idx) => (
                      <TableRow key={idx}>
                        <TableCell>{row.PO_ID}</TableCell>
                        <TableCell>{row.stated_reason}</TableCell>
                        <TableCell>{row["Match/Not"]}</TableCell>
                        <TableCell>{row.Comments}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
}

export default ValidationKPIs;
