import React, { useState } from "react";
import { Box, Typography, Button } from "@mui/material";
import TaggingKPIs from "./TaggingKPIs";
import { BACKEND_URL } from "../utils/backend";

function Tagging() {
  const [running, setRunning] = useState(false);
  const [runMsg, setRunMsg] = useState("");
  const [refresh, setRefresh] = useState(0);

  const handleRunWorkflow = async () => {
    setRunning(true);
    setRunMsg("");
    try {
      const res = await fetch(`${BACKEND_URL}/run-tagging`, { method: "POST" });
      const data = await res.json();
      setRunMsg(data.status === "success" ? "Tagging workflow completed!" : "Workflow failed.");
      setRefresh(r => r + 1); // trigger refresh
    } catch (e) {
      setRunMsg("Error running workflow.");
    }
    setRunning(false);
  };

  return (
    <Box sx={{ 
      minHeight: '100vh',
      backgroundColor: '#f8f9fa',
      p: 3
    }}>
      {/* Header Section */}
      <Box sx={{ 
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        borderRadius: '16px',
        p: 4,
        mb: 3,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        boxShadow: '0 8px 32px rgba(102, 126, 234, 0.2)'
      }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Box sx={{ 
            backgroundColor: 'rgba(255,255,255,0.2)', 
            borderRadius: '12px', 
            p: 1, 
            mr: 3,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            <Typography sx={{ fontSize: '24px' }}>🚩</Typography>
          </Box>
          <Box>
            <Typography 
              variant="h4" 
              sx={{ 
                color: 'white', 
                fontWeight: 700,
                mb: 0.5,
                letterSpacing: '-0.02em'
              }}
            >
              Potential Risk Flagger
            </Typography>
            <Typography 
              variant="subtitle1" 
              sx={{ 
                color: 'rgba(255,255,255,0.9)', 
                fontWeight: 400 
              }}
            >
              AI-Powered Transaction Risk Detection & Analysis
            </Typography>
          </Box>
        </Box>
        
        <Button 
          variant="contained"
          onClick={handleRunWorkflow} 
          disabled={running}
          sx={{
            backgroundColor: 'rgba(255,255,255,0.15)',
            color: 'white',
            border: '1px solid rgba(255,255,255,0.2)',
            borderRadius: '12px',
            px: 3,
            py: 1.5,
            fontWeight: 600,
            fontSize: '14px',
            textTransform: 'none',
            backdropFilter: 'blur(10px)',
            '&:hover': {
              backgroundColor: 'rgba(255,255,255,0.25)',
              transform: 'translateY(-1px)',
              boxShadow: '0 4px 20px rgba(0,0,0,0.15)'
            },
            '&:disabled': {
              backgroundColor: 'rgba(255,255,255,0.1)',
              color: 'rgba(255,255,255,0.6)'
            }
          }}
        >
          {running ? '⏳ Running...' : '▶ Run Analysis'}
        </Button>
      </Box>
      
      {runMsg && (
        <Box sx={{ 
          backgroundColor: '#e8f5e8',
          border: '1px solid #c8e6c9',
          borderRadius: '12px',
          p: 2,
          mb: 3,
          display: 'flex',
          alignItems: 'center'
        }}>
          <Typography sx={{ color: '#2e7d32', fontWeight: 500, fontSize: '14px' }}>
            ✅ {runMsg}
          </Typography>
        </Box>
      )}
      
      <TaggingKPIs refresh={refresh} />
    </Box>
  );
}

export default Tagging;
